import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useContactPopupContext } from '../../contexts/ContactPopupContext';

const HeroSection = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  padding: 120px 0 80px;
  position: relative;
  overflow: hidden;
`;

const Container = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  width: 100%;
`;

const HeroGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 80px;
  align-items: center;
  
  @media (max-width: 1024px) {
    grid-template-columns: 1fr;
    gap: 60px;
  }
`;

const ContentSection = styled.div`
  @media (max-width: 1024px) {
    order: 2;
  }
`;

const Title = styled(motion.h1)`
  font-size: 72px;
  font-weight: 700;
  line-height: 1.1;
  margin-bottom: 32px;
  color: #FFFFFF;
  letter-spacing: -0.02em;
  
  @media (max-width: 1200px) {
    font-size: 56px;
  }
  
  @media (max-width: 768px) {
    font-size: 40px;
  }
`;

const ConsultationButton = styled(motion.a)`
  display: inline-block;
  color: #3A5AFF;
  font-size: 18px;
  font-weight: 500;
  text-decoration: none;
  padding: 16px 0;
  border: none;
  background: none;
  font-family: inherit;
  cursor: pointer;
  border-bottom: 2px solid #3A5AFF;
  transition: all 0.3s ease;
  
  &:hover {
    color: #5A7AFF;
    border-bottom-color: #5A7AFF;
  }
`;

const ProcessSection = styled.div`
  @media (max-width: 1024px) {
    order: 1;
  }
`;

const ProcessDiagram = styled.div`
  position: relative;
  width: 100%;
  height: 400px;
  
  @media (max-width: 768px) {
    height: 300px;
  }
`;

const MainLine = styled.div`
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(90deg, #2A2A2A 0%, #BFBFBF 50%, #2A2A2A 100%);
  transform: translateY(-50%);
`;

const ProcessStep = styled(motion.div)`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background: #2A2A2A;
  padding: 12px 20px;
  border: 1px solid #BFBFBF;
  color: #FFFFFF;
  font-size: 14px;
  font-weight: 500;
  white-space: nowrap;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:hover {
    background: #3A3A3A;
    border-color: #3A5AFF;
  }
`;

const SupportBlock = styled(motion.div)`
  position: absolute;
  bottom: 60px;
  left: 50%;
  transform: translateX(-50%);
  background: #2A2A2A;
  border: 1px solid #BFBFBF;
  padding: 20px;
  min-width: 200px;
`;

const SupportTitle = styled.div`
  color: #FFFFFF;
  font-size: 16px;
  font-weight: 600;
  margin-bottom: 12px;
  text-align: center;
`;

const SupportItems = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

const SupportItem = styled.div`
  color: #E0E0E0;
  font-size: 14px;
  text-align: center;
`;

const Arrow = styled.div`
  position: absolute;
  width: 2px;
  background: #BFBFBF;
  
  &.vertical {
    height: 40px;
    left: 50%;
    transform: translateX(-50%);
  }
  
  &.diagonal {
    width: 2px;
    height: 60px;
    background: #BFBFBF;
    transform: rotate(45deg);
    transform-origin: top left;
  }
`;

const ContactInfo = styled.div`
  margin-top: 40px;
  text-align: center;
  color: #E0E0E0;
  font-size: 16px;
  line-height: 1.6;
`;

const PhoneLink = styled.a`
  color: #3A5AFF;
  text-decoration: none;
  font-weight: 500;
  
  &:hover {
    color: #5A7AFF;
  }
`;

const Hero = () => {
  const { openPopup } = useContactPopupContext();
  
  const steps = [
    { name: 'Идея', position: '10%' },
    { name: 'Аналитика', position: '25%' },
    { name: 'Дизайн', position: '40%' },
    { name: 'Разработка', position: '55%' },
    { name: 'Сайт', position: '70%' },
    { name: 'Продажи', position: '85%' }
  ];

  const supportItems = ['SEO', 'Маркетинг', 'Реклама'];

  return (
    <HeroSection>
      <Container>
        <HeroGrid>
          <ContentSection>
            <Title
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              Сайты и полноценные веб-системы <br></br>под ключ
            </Title>
            
            <ConsultationButton
              as="button"
              onClick={openPopup}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.6 }}
              whileHover={{ scale: 1.02 }}
            >
              Бесплатная консультация по деталям реализации
            </ConsultationButton>
          </ContentSection>

          <ProcessSection>
            <ProcessDiagram>
              <MainLine />
              
              {steps.map((step, index) => (
                <ProcessStep
                  key={step.name}
                  style={{ left: step.position }}
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ duration: 0.5, delay: 0.8 + index * 0.1 }}
                  whileHover={{ scale: 1.05 }}
                >
                  {step.name}
                </ProcessStep>
              ))}
              
              {/* Стрелка от "Продажи" к "Поддержка" */}
              <Arrow 
                className="vertical"
                style={{ 
                  bottom: '100px',
                  left: '85%'
                }}
              />
              
              {/* Блок поддержки */}
              <SupportBlock
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 1.4 }}
                whileHover={{ scale: 1.02 }}
              >
                <SupportTitle>Поддержка</SupportTitle>
                <SupportItems>
                  {supportItems.map((item, index) => (
                    <SupportItem key={item}>{item}</SupportItem>
                  ))}
                </SupportItems>
              </SupportBlock>
              
              {/* Стрелка от "Поддержка" обратно к "Сайт" */}
              <Arrow 
                className="diagonal"
                style={{ 
                  bottom: '120px',
                  left: '70%',
                  transform: 'rotate(-45deg)'
                }}
              />
            </ProcessDiagram>
            

          </ProcessSection>
        </HeroGrid>
      </Container>
    </HeroSection>
  );
};

export default Hero;
