import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const ProcessSection = styled.section`
  padding: 120px 0;
  background-color: #151515;
`;

const Container = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 80px;
`;

const Title = styled(motion.h2)`
  font-size: 40px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 24px;
  letter-spacing: -0.01em;
  
  @media (max-width: 768px) {
    font-size: 28px;
  }
`;

const ProcessGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 60px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 40px;
  }
`;

const ProcessColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 40px;
`;

const ProcessStep = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  padding: 32px;
  position: relative;
  transition: all 0.3s ease;
  
  &:hover {
    border-color: #BFBFBF;
    transform: translateY(-2px);
  }
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 4px;
    height: 100%;
    background: #3A5AFF;
    opacity: 0;
    transition: opacity 0.3s ease;
  }
  
  &:hover::before {
    opacity: 1;
  }
`;

const StepNumber = styled.div`
  font-size: 48px;
  font-weight: 700;
  color: #BFBFBF;
  margin-bottom: 16px;
  line-height: 1;
  
  @media (max-width: 768px) {
    font-size: 36px;
  }
`;

const StepTitle = styled.h3`
  font-size: 24px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 12px;
  
  @media (max-width: 768px) {
    font-size: 20px;
  }
`;

const StepDescription = styled.p`
  font-size: 16px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
`;

const Process = () => {
  const leftColumnSteps = [
    {
      number: '01',
      title: 'Знакомство',
      description: 'Обсуждаем цели, задачи, рынок и ваши ожидания от проекта'
    },
    {
      number: '02',
      title: 'Аналитика',
      description: 'Исследуем конкурентов, аудиторию и рынок. Проводим UX-исследования и создаем детальную стратегию проекта'
    },
    {
      number: '03',
      title: 'Прототип',
      description: 'Создаём структуру и UX-макеты. Разрабатываем информационную архитектуру и пользовательские сценарии'
    }
  ];

  const rightColumnSteps = [
    {
      number: '04',
      title: 'Дизайн',
      description: 'Разрабатываем UI, визуальный стиль и брендинг. Создаем адаптивные макеты для всех устройств'
    },
    {
      number: '05',
      title: 'Разработка',
      description: 'Собираем сайт/сервис на современном стеке технологий. Обеспечиваем высокую производительность и безопасность'
    },
    {
      number: '06',
      title: 'Запуск и поддержка',
      description: 'Выводим проект в онлайн и сопровождаем его развитие. Обеспечиваем техническую поддержку и обновления'
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <ProcessSection id="process">
      <Container>
        <SectionHeader>
          <Title
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            Как мы работаем
          </Title>
        </SectionHeader>

        <ProcessGrid
          as={motion.div}
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
        >
          <ProcessColumn>
            {leftColumnSteps.map((step, index) => (
              <ProcessStep
                key={step.number}
                variants={itemVariants}
                whileHover={{ scale: 1.02 }}
              >
                <StepNumber>{step.number}</StepNumber>
                <StepTitle>{step.title}</StepTitle>
                <StepDescription>{step.description}</StepDescription>
              </ProcessStep>
            ))}
          </ProcessColumn>

          <ProcessColumn>
            {rightColumnSteps.map((step, index) => (
              <ProcessStep
                // key={step.number}
                variants={itemVariants}
                whileHover={{ scale: 1.02 }}
              >
                <StepNumber>{step.number}</StepNumber>
                <StepTitle>{step.title}</StepTitle>
                <StepDescription>{step.description}</StepDescription>
              </ProcessStep>
            ))}
          </ProcessColumn>
        </ProcessGrid>
      </Container>
    </ProcessSection>
  );
};

export default Process;
