import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

const ProjectPageContainer = styled.div`
  background-color: #151515;
  min-height: 100vh;
`;

const MinimalHeader = styled.header`
  padding: 24px 0;
  background-color: #151515;
  border-bottom: 1px solid #2A2A2A;
`;

const HeaderContainer = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const Logo = styled(Link)`
  font-size: 24px;
  font-weight: 700;
  color: #FFFFFF;
  text-decoration: none;
  transition: color 0.2s ease;
  
  &:hover {
    color: #3A5AFF;
  }
`;

const BackButton = styled(Link)`
  display: flex;
  align-items: center;
  gap: 8px;
  color: #E0E0E0;
  font-size: 16px;
  font-weight: 400;
  text-decoration: none;
  transition: color 0.2s ease;
  
  &:hover {
    color: #FFFFFF;
  }
`;

const HeroSection = styled.section`
  padding: 80px 0 120px;
  background-color: #151515;
`;

const HeroContainer = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const HeroGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 80px;
  align-items: center;
  
  @media (max-width: 1024px) {
    grid-template-columns: 1fr;
    gap: 60px;
  }
`;

const HeroContent = styled.div``;

const HeroTitle = styled(motion.h1)`
  font-size: 64px;
  font-weight: 700;
  color: #FFFFFF;
  margin-bottom: 24px;
  line-height: 1.1;
  letter-spacing: -0.02em;
  
  @media (max-width: 768px) {
    font-size: 40px;
  }
`;

const HeroSubtitle = styled(motion.p)`
  font-size: 20px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 40px;
`;

const HeroCTA = styled(motion.a)`
  display: inline-block;
  padding: 16px 32px;
  background: #FFFFFF;
  color: #151515;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;
  border: 1px solid #FFFFFF;
  
  &:hover {
    background: transparent;
    color: #FFFFFF;
  }
`;

const HeroVisual = styled(motion.div)`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 20px;
  
  @media (max-width: 1024px) {
    order: -1;
  }
`;

const MockupDesktop = styled.div`
  width: 300px;
  height: 200px;
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 8px;
    left: 8px;
    right: 8px;
    height: 20px;
    background: #3A3A3A;
    border-radius: 4px;
  }
`;

const MockupMobile = styled.div`
  width: 80px;
  height: 160px;
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 12px;
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 8px;
    left: 8px;
    right: 8px;
    height: 12px;
    background: #3A3A3A;
    border-radius: 6px;
  }
`;

const Section = styled.section`
  padding: 120px 0;
  background-color: #151515;
`;

const Container = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const SectionTitle = styled(motion.h2)`
  font-size: 40px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 40px;
  letter-spacing: -0.01em;
  
  @media (max-width: 768px) {
    font-size: 28px;
  }
`;

const SectionText = styled(motion.p)`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 24px;
  max-width: 800px;
`;

const TasksList = styled(motion.ul)`
  list-style: none;
  padding: 0;
  margin: 0;
`;

const TaskItem = styled(motion.li)`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 16px;
  padding-left: 24px;
  position: relative;
  
  &::before {
    content: '•';
    color: #3A5AFF;
    font-weight: bold;
    position: absolute;
    left: 0;
  }
`;

const WorkflowGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 40px;
  margin-top: 40px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 32px;
  }
`;

const WorkflowCard = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  padding: 32px;
  border-radius: 8px;
`;

const WorkflowTitle = styled.h3`
  font-size: 24px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 16px;
`;

const WorkflowDescription = styled.p`
  font-size: 16px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
`;

const GalleryGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 40px;
  margin-top: 40px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 32px;
  }
`;

const GalleryItem = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  overflow: hidden;
`;

const GalleryImage = styled.div`
  width: 100%;
  height: 200px;
  background: linear-gradient(135deg, #2A2A2A 0%, #3A3A3A 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  color: #BFBFBF;
  font-size: 14px;
`;

const GalleryCaption = styled.p`
  padding: 16px;
  font-size: 14px;
  color: #BFBFBF;
  font-weight: 300;
  line-height: 1.4;
  margin: 0;
`;

const ResultsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 40px;
  margin-top: 40px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 32px;
  }
`;

const ResultCard = styled(motion.div)`
  text-align: center;
  padding: 32px 24px;
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
`;

const ResultNumber = styled.div`
  font-size: 48px;
  font-weight: 700;
  color: #3A5AFF;
  margin-bottom: 16px;
  line-height: 1;
`;

const ResultText = styled.div`
  font-size: 16px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.4;
`;

const QuoteCard = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  padding: 32px;
  margin-top: 40px;
  position: relative;
  
  &::before {
    content: '"';
    position: absolute;
    top: 16px;
    left: 24px;
    font-size: 48px;
    color: #3A5AFF;
    font-weight: 700;
  }
`;

const QuoteText = styled.p`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin: 0;
  padding-left: 40px;
  font-style: italic;
`;

const ContactSection = styled.section`
  padding: 120px 0;
  background-color: #1A1A1A;
  text-align: center;
`;

const ContactTitle = styled(motion.h2)`
  font-size: 40px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 24px;
  letter-spacing: -0.01em;
`;

const ContactText = styled(motion.p)`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 40px;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const ContactCTA = styled(motion.a)`
  display: inline-block;
  padding: 16px 32px;
  background: #FFFFFF;
  color: #151515;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;
  border: 1px solid #FFFFFF;
  margin-bottom: 40px;
  
  &:hover {
    background: transparent;
    color: #FFFFFF;
  }
`;

const NavigationLinks = styled(motion.div)`
  display: flex;
  justify-content: center;
  gap: 40px;
  
  @media (max-width: 768px) {
    flex-direction: column;
    gap: 20px;
  }
`;

const NavLink = styled(Link)`
  color: #E0E0E0;
  font-size: 16px;
  font-weight: 400;
  text-decoration: none;
  transition: color 0.2s ease;
  
  &:hover {
    color: #FFFFFF;
  }
`;

const ProjectPage = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <ProjectPageContainer>
      {/* Минималистичный хеддер */}
      <MinimalHeader>
        <HeaderContainer>
          <Logo to="/">CORE</Logo>
          <BackButton to="/projects">
            ← Все проекты
          </BackButton>
        </HeaderContainer>
      </MinimalHeader>

      {/* Hero-блок */}
      <HeroSection>
        <HeroContainer>
          <HeroGrid
            as={motion.div}
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            <HeroContent>
              <HeroTitle variants={itemVariants}>
                Агентство недвижимости SOLTERRA
              </HeroTitle>
              <HeroSubtitle variants={itemVariants}>
                Разработка фирменного стиля, лендинга и полного сайта агентства
              </HeroSubtitle>
              <HeroCTA
                href="https://solterra.example.com"
                target="_blank"
                rel="noopener noreferrer"
                variants={itemVariants}
                whileHover={{ scale: 1.02 }}
              >
                Открыть сайт проекта
              </HeroCTA>
            </HeroContent>
            <HeroVisual variants={itemVariants}>
              <MockupDesktop />
              <MockupMobile />
            </HeroVisual>
          </HeroGrid>
        </HeroContainer>
      </HeroSection>

      {/* О клиенте */}
      <Section>
        <Container>
          <SectionTitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            О проекте
          </SectionTitle>
          <SectionText
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            SOLTERRA — компания, работающая с жилыми комплексами бизнес-класса. 
            Мы помогли агентству пройти полный цикл: от разработки бренда до запуска сайта, 
            который стал ключевым инструментом продаж.
          </SectionText>
          <SectionText
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.3 }}
            viewport={{ once: true }}
          >
            Проект включал создание фирменного стиля, запуск лендинга для быстрого старта 
            и разработку полноценного сайта агентства с каталогом объектов и системой управления заявками.
          </SectionText>
        </Container>
      </Section>

      {/* Цели и задачи */}
      <Section>
        <Container>
          <SectionTitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            Задачи
          </SectionTitle>
          <TasksList
            as={motion.ul}
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            <TaskItem variants={itemVariants}>
              Сформировать фирменный стиль и позиционирование
            </TaskItem>
            <TaskItem variants={itemVariants}>
              Запустить быстрый лендинг для теста гипотез и первых заявок
            </TaskItem>
            <TaskItem variants={itemVariants}>
              Подключить рекламу и SEO для старта продаж
            </TaskItem>
            <TaskItem variants={itemVariants}>
              Разработать сайт агентства с каталогами ЖК и брокеров
            </TaskItem>
            <TaskItem variants={itemVariants}>
              Организовать прозрачный процесс разработки
            </TaskItem>
            <TaskItem variants={itemVariants}>
              Обеспечить ускоренный запуск (с доплатой клиента)
            </TaskItem>
          </TasksList>
        </Container>
      </Section>

      {/* Этапы работы */}
      <Section>
        <Container>
          <SectionTitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            Этапы работы
          </SectionTitle>
          <WorkflowGrid
            as={motion.div}
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            <WorkflowCard variants={itemVariants}>
              <WorkflowTitle>Этап 1. Брендинг</WorkflowTitle>
              <WorkflowDescription>
                Разработка логотипа, айдентики и фирменного стиля. 
                Формирование позиционирования: миссия, ценности, tone of voice. 
                Подготовка брендбука.
              </WorkflowDescription>
            </WorkflowCard>
            <WorkflowCard variants={itemVariants}>
              <WorkflowTitle>Этап 2. Лендинг + быстрый запуск</WorkflowTitle>
              <WorkflowDescription>
                Создание одностраничного лендинга для генерации первых заявок. 
                Настройка рекламных кампаний (Google Ads / Яндекс). 
                Базовое SEO. Получены первые заявки уже на этапе разработки основного сайта.
              </WorkflowDescription>
            </WorkflowCard>
            <WorkflowCard variants={itemVariants}>
              <WorkflowTitle>Этап 3. Сайт агентства недвижимости</WorkflowTitle>
              <WorkflowDescription>
                Архитектура сайта: главная, каталог ЖК, брокеры, о компании. 
                Упаковка объектов недвижимости с фильтрацией. 
                Представление брокеров. Интеграция с CRM для учёта заявок.
              </WorkflowDescription>
            </WorkflowCard>
            <WorkflowCard variants={itemVariants}>
              <WorkflowTitle>Этап 4. Организация процесса</WorkflowTitle>
              <WorkflowDescription>
                Прозрачное планирование: доступ заказчика к Trello/Notion. 
                Еженедельные отчёты и согласования. 
                Отдельная опция — ускоренный запуск: клиент оплатил за приоритет и получил сайт быстрее.
              </WorkflowDescription>
            </WorkflowCard>
          </WorkflowGrid>
        </Container>
      </Section>

      {/* Визуализация проекта */}
      <Section>
        <Container>
          <SectionTitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            Дизайн и реализация
          </SectionTitle>
          <GalleryGrid
            as={motion.div}
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            <GalleryItem variants={itemVariants}>
              <GalleryImage>Фирменный стиль</GalleryImage>
              <GalleryCaption>
                Айдентика и элементы фирменного стиля (логотип, брендбук)
              </GalleryCaption>
            </GalleryItem>
            <GalleryItem variants={itemVariants}>
              <GalleryImage>Лендинг Desktop</GalleryImage>
              <GalleryCaption>
                Лендинг — быстрый запуск продаж
              </GalleryCaption>
            </GalleryItem>
            <GalleryItem variants={itemVariants}>
              <GalleryImage>Лендинг Mobile</GalleryImage>
              <GalleryCaption>
                Мобильная версия лендинга
              </GalleryCaption>
            </GalleryItem>
            <GalleryItem variants={itemVariants}>
              <GalleryImage>Каталог ЖК</GalleryImage>
              <GalleryCaption>
                Каталог жилых комплексов — удобный поиск и фильтрация
              </GalleryCaption>
            </GalleryItem>
            <GalleryItem variants={itemVariants}>
              <GalleryImage>Страница брокера</GalleryImage>
              <GalleryCaption>
                Страница брокера с портфолио и контактами
              </GalleryCaption>
            </GalleryItem>
            <GalleryItem variants={itemVariants}>
              <GalleryImage>Главная страница</GalleryImage>
              <GalleryCaption>
                Главная страница сайта агентства
              </GalleryCaption>
            </GalleryItem>
          </GalleryGrid>
        </Container>
      </Section>

      {/* Результаты */}
      <Section>
        <Container>
          <SectionTitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            Результаты проекта
          </SectionTitle>
          <ResultsGrid
            as={motion.div}
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            <ResultCard variants={itemVariants}>
              <ResultNumber>+35%</ResultNumber>
              <ResultText>заявок после запуска лендинга и рекламы</ResultText>
            </ResultCard>
            <ResultCard variants={itemVariants}>
              <ResultNumber>ТОП-10</ResultNumber>
              <ResultText>по запросам «купить квартиру [регион]»</ResultText>
            </ResultCard>
            <ResultCard variants={itemVariants}>
              <ResultNumber>100%</ResultNumber>
              <ResultText>сайт стал основным каналом продаж</ResultText>
            </ResultCard>
          </ResultsGrid>
          
          <QuoteCard
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            <QuoteText>
              Благодаря прозрачности процесса мы всегда знали, на каком этапе работа. 
              Ускоренный запуск позволил стартовать раньше конкурентов.
            </QuoteText>
          </QuoteCard>
        </Container>
      </Section>

      {/* Контакты и возврат */}
      <ContactSection>
        <Container>
          <ContactTitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            Хотите запустить сайт для агентства недвижимости с быстрым результатом?
          </ContactTitle>
          <ContactText
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            Обсудим ваш проект и создадим решение, которое принесёт результат уже в первые недели после запуска.
          </ContactText>
          <ContactCTA
            href="/contact"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.3 }}
            viewport={{ once: true }}
            whileHover={{ scale: 1.02 }}
          >
            Обсудить проект
          </ContactCTA>
          <NavigationLinks
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
            viewport={{ once: true }}
          >
            <NavLink to="/projects">← Все проекты</NavLink>
            <NavLink to="/">Главная</NavLink>
          </NavigationLinks>
        </Container>
      </ContactSection>
    </ProjectPageContainer>
  );
};

export default ProjectPage;
