sourcesContent:
import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

const ProjectPageContainer = styled.div`
  background-color: #151515;
  min-height: 100vh;
`;

const MinimalHeader = styled.header`
  padding: 24px 0;
  background-color: #151515;
  border-bottom: 1px solid #2A2A2A;
`;

const HeaderContainer = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const Logo = styled(Link)`
  font-size: 24px;
  font-weight: 700;
  color: #FFFFFF;
  text-decoration: none;
  transition: color 0.2s ease;
  
  &:hover {
    color: #3A5AFF;
  }
`;

const BackButton = styled(Link)`
  display: flex;
  align-items: center;
  gap: 8px;
  color: #E0E0E0;
  font-size: 16px;
  font-weight: 400;
  text-decoration: none;
  transition: color 0.2s ease;
  
  &:hover {
    color: #FFFFFF;
  }
`;

const HeroSection = styled.section`
  padding: 80px 0 120px;
  background-color: #151515;
`;

const HeroContainer = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const HeroGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 80px;
  align-items: center;
  
  @media (max-width: 1024px) {
    grid-template-columns: 1fr;
    gap: 60px;
  }
`;

const HeroContent = styled.div``;

const HeroTitle = styled(motion.h1)`
  font-size: 64px;
  font-weight: 700;
  color: #FFFFFF;
  margin-bottom: 24px;
  line-height: 1.1;
  letter-spacing: -0.02em;
  
  @media (max-width: 768px) {
    font-size: 40px;
  }
`;

const HeroSubtitle = styled(motion.p)`
  font-size: 20px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 40px;
`;

const HeroCTA = styled(motion.a)`
  display: inline-block;
  padding: 16px 32px;
  background: #FFFFFF;
  color: #151515;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;
  border: 1px solid #FFFFFF;
  
  &:hover {
    background: transparent;
    color: #FFFFFF;
  }
`;

const HeroVisual = styled(motion.div)`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 20px;
  
  @media (max-width: 1024px) {
    order: -1;
  }
`;

const MockupDesktop = styled.div`
  width: 300px;
  height: 200px;
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 8px;
    left: 8px;
    right: 8px;
    height: 20px;
    background: #3A3A3A;
    border-radius: 4px;
  }
`;

const MockupMobile = styled.div`
  width: 80px;
  height: 160px;
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 12px;
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 8px;
    left: 8px;
    right: 8px;
    height: 12px;
    background: #3A3A3A;
    border-radius: 6px;
  }
`;

const Section = styled.section`
  padding: 120px 0;
  background-color: #151515;
`;

const Container = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const SectionTitle = styled(motion.h2)`
  font-size: 40px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 40px;
  letter-spacing: -0.01em;
  
  @media (max-width: 768px) {
    font-size: 28px;
  }
`;

const SectionText = styled(motion.p)`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 24px;
  max-width: 800px;
`;

const TasksList = styled(motion.ul)`
  list-style: none;
  padding: 0;
  margin: 0;
`;

const TaskItem = styled(motion.li)`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 16px;
  padding-left: 24px;
  position: relative;
  
  &::before {
    content: 'â€¢';
    color: #3A5AFF;
    font-weight: bold;
    position: absolute;
    left: 0;
  }
`;

const WorkflowGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 40px;
  margin-top: 40px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 32px;
  }
`;

const WorkflowCard = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  padding: 32px;
  border-radius: 8px;
`;

const WorkflowTitle = styled.h3`
  font-size: 24px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 16px;
`;

const WorkflowDescription = styled.p`
  font-size: 16px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
`;

const GalleryGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 40px;
  margin-top: 40px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 32px;
  }
`;

const GalleryItem = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  overflow: hidden;
`;

const GalleryImage = styled.div`
  width: 100%;
  height: 200px;
  background: linear-gradient(135deg, #2A2A2A 0%, #3A3A3A 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  color: #BFBFBF;
  font-size: 14px;
`;

const GalleryCaption = styled.p`
  padding: 16px;
  font-size: 14px;
  color: #BFBFBF;
  font-weight: 300;
  line-height: 1.4;
  margin: 0;
`;

const ResultsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 40px;
  margin-top: 40px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 32px;
  }
`;

const ResultCard = styled(motion.div)`
  text-align: center;
  padding: 32px 24px;
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
`;

const ResultNumber = styled.div`
  font-size: 48px;
  font-weight: 700;
  color: #3A5AFF;
  margin-bottom: 16px;
  line-height: 1;
`;

const ResultText = styled.div`
  font-size: 16px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.4;
`;

const QuoteCard = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  padding: 32px;
  margin-top: 40px;
  position: relative;
  
  &::before {
    content: '"';
    position: absolute;
    top: 16px;
    left: 24px;
    font-size: 48px;
    color: #3A5AFF;
    font-weight: 700;
  }
`;

const QuoteText = styled.p`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin: 0;
  padding-left: 40px;
  font-style: italic;
`;

const ContactSection = styled.section`
  padding: 120px 0;
  background-color: #1A1A1A;
  text-align: center;
`;

const ContactTitle = styled(motion.h2)`
  font-size: 40px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 24px;
  letter-spacing: -0.01em;
`;

const ContactText = styled(motion.p)`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 40px;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const ContactCTA = styled(motion.a)`
  display: inline-block;
  padding: 16px 32px;
  background: #FFFFFF;
  color: #151515;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;
  border: 1px solid #FFFFFF;
  margin-bottom: 40px;
  
  &:hover {
    background: transparent;
    color: #FFFFFF;
  }
`;

const NavigationLinks = styled(motion.div)`
  display: flex;
  justify-content: center;
  gap: 40px;
  
  @media (max-width: 768px) {
    flex-direction: column;
    gap: 20px;
  }
`;

const NavLink = styled(Link)`
  color: #E0E0E0;
  font-size: 16px;
  font-weight: 400;
  text-decoration: none;
  transition: color 0.2s ease;
  
  &:hover {
    color: #FFFFFF;
  }
`;

const ProjectPage = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <ProjectPageContainer>
      {/* ÐœÐ¸Ð½Ð¸Ð¼Ð°Ð»Ð¸ÑÑ‚Ð¸Ñ‡Ð½Ñ‹Ð¹ Ñ…ÐµÐ´Ð´ÐµÑ€ */}
      <MinimalHeader>
        <HeaderContainer>
          <Logo to="/">CORE</Logo>
          <BackButton to="/projects">
            â† Ð’ÑÐµ Ð¿Ñ€Ð¾ÐµÐºÑ‚Ñ‹
          </BackButton>
        </HeaderContainer>
      </MinimalHeader>

      {/* Hero-Ð±Ð»Ð¾Ðº */}
      <HeroSection>
        <HeroContainer>
          <HeroGrid
            as={motion.div}
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            <HeroContent>
              <HeroTitle variants={itemVariants}>
                ÐÐ³ÐµÐ½Ñ‚ÑÑ‚Ð²Ð¾ Ð½ÐµÐ´Ð²Ð¸Ð¶Ð¸Ð¼Ð¾ÑÑ‚Ð¸ SOLTERRA
