import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { useContactPopupContext } from '../../contexts/ContactPopupContext';

// Container
const ProjectPageContainer = styled.div`
  background-image: url('/images/my-kit/bg4.jpg');
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  background-attachment: scroll;
  min-height: 100vh;
  position: relative;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    // background: rgba(21, 21, 21, 0.85);
    z-index: 0;
  }
  
  > * {
    position: relative;
    z-index: 1;
  }
`;

// Minimal Header
const MinimalHeader = styled.header`
  padding: 24px 0;
  background-color: transparent;
  // border-bottom: 1px solid rgba(42, 42, 42, 0.5);
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  z-index: 10;
`;

const HeaderContainer = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const Logo = styled(Link)`
  font-family: 'Neue Haas Grotesk Display', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  font-size: 24px;
  font-weight: 700;
  color: #FFFFFF;
  text-decoration: none;
  transition: color 0.2s ease;
  
  &:hover {
    color: #3A5AFF;
  }
`;

const BackButton = styled(Link)`
  display: flex;
  align-items: center;
  gap: 8px;
  color: #FFFFFF;
  font-size: 16px;
  font-weight: 400;
  text-decoration: none;
  transition: color 0.2s ease;
  
  &:hover {
    color: #FFFFFF;
  }
`;


// Проблема (вступление)
const ProblemSection = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  padding: 120px 0 80px;
  position: relative;
  overflow: hidden;
  // background: linear-gradient(135deg, rgba(21, 21, 21, 0.9) 0%, rgba(42, 42, 42, 0.7) 100%);
`;

const ProblemBackground = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: url('/images/pharmacy-hero.jpg');
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  z-index: -1;
`;

const Container = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  width: 100%;
  position: relative;
  z-index: 2;
  margin-top: 80px;
  
  @media (max-width: 768px) {
    padding: 0 24px;
    margin-top: 60px;
  }
`;

const ProblemContent = styled.div`
  display: grid;
  grid-template-columns: 1.5fr 1fr;
  gap: 80px;
  align-items: center;
  width: 100%;
  
  @media (max-width: 1024px) {
    grid-template-columns: 1fr;
    gap: 40px;
    text-align: center;
  }
`;

const ProblemText = styled.div`
  // max-width: 600px;
`;

const ProblemImage = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  
  img {
    max-width: 90%;
    height: auto;
  }
`;

const Title = styled(motion.h1)`
  font-family: 'Akkurat Pro', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  font-size: 64px;
  font-weight: 700;
  line-height: 1.1;
  margin-bottom: 32px;
  color: #FFFFFF;
  letter-spacing: -0.02em;
  
  @media (max-width: 768px) {
    font-size: 40px;
  }
`;

const ProblemDescription = styled(motion.p)`
  font-size: 20px;
  color: #FFFFFF;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 48px;
  max-width: 700px;
  
  @media (max-width: 768px) {
    font-size: 18px;
    margin-bottom: 40px;
  }
`;

const HeroButtons = styled(motion.div)`
  display: flex;
  gap: 16px;
  flex-wrap: wrap;
  margin-top: 32px;
  
  @media (max-width: 768px) {
    flex-direction: column;
    gap: 12px;
  }
`;

const HeroButton = styled(motion.button)`
  padding: 12px 24px;
  background: transparent;
  color: #FFFFFF;
  font-size: 16px;
  font-weight: 500;
  border: 1px solid #FFFFFF;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
  white-space: nowrap;
  
  &:hover {
    color: #FFFFFF;
    // border-color: #3A5AFF;
    background:rgba(255, 195, 254, 0.26);
  }
  
  @media (max-width: 768px) {
    font-size: 14px;
    padding: 10px 20px;
    width: 100%;
  }
`;

const PrimaryHeroButton = styled(HeroButton)`
  background: #3A5AFF;
  color: #FFFFFF;
  border-color: #3A5AFF;
  
  &:hover {
    background: #3A5AFF;
    color: #FFFFFF;
    box-shadow: 0 0 10px 0 rgba(58, 91, 255, 0.5);
  }
`;

// Основные секции  
const Section = styled.section`
  padding: 120px 0;
  background-color: transparent;
`;

const SectionContainer = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const SectionTitle = styled.h2`
  font-family: 'Akkurat Pro', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  font-size: 48px;
  font-weight: 700;
  color: #FFFFFF;
  margin-bottom: 48px;
  letter-spacing: -0.02em;
  
  @media (max-width: 768px) {
    font-size: 32px;
    margin-bottom: 32px;
  }
`;

const SectionText = styled.p`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 24px;
  max-width: 800px;
`;

// Решение - 3 колонки
const SolutionGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 60px;
  margin-top: 48px;
  
  @media (max-width: 1024px) {
    grid-template-columns: 1fr;
    gap: 40px;
  }
`;

const SolutionCard = styled.div`
  text-align: center;
`;

const SolutionImage = styled.div`
  width: 100%;
  height: 200px;
  background: linear-gradient(135deg, #2A2A2A 0%, #3A3A3A 100%);
  border-radius: 8px;
  margin-bottom: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #BFBFBF;
  font-size: 14px;
`;

const SolutionTitle = styled.h3`
  font-size: 24px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 16px;
`;

const SolutionText = styled.p`
  font-size: 16px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
`;

// Ключевые функции - сетка 3x2
const FunctionsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 40px;
  margin-top: 48px;
  
  @media (max-width: 1024px) {
    grid-template-columns: repeat(2, 1fr);
    gap: 32px;
  }
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 24px;
  }
`;

const FunctionCard = styled(motion.div)`
  background:rgb(67, 186, 255);
  border: 1px solid #3A5AFF;
  border-radius: 8px;
  padding: 32px 24px;
  text-align: center;
  transition: all 0.3s ease;
  
  &:hover {
    border-color: #3A5AFF;
    transform: translateY(-4px);
  }
`;

const FunctionIcon = styled.div`
  width: 60px;
  height: 60px;
  background: #3A5AFF;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 20px;
  color: #FFFFFF;
  font-size: 24px;
`;

const FunctionTitle = styled.h3`
  font-size: 18px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 12px;
`;

const FunctionDescription = styled.p`
  font-size: 14px;
  color: #1b38c8;
  font-weight: 500;
  line-height: 1.5;
`;

// Процесс работы - карточки
const ProcessGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 60px;
  margin-top: 48px;
  
  @media (max-width: 1024px) {
    grid-template-columns: 1fr;
    gap: 40px;
  }
`;

const ProcessCard = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  overflow: hidden;
`;

const ProcessImage = styled.div`
  width: 100%;
  height: 200px;
  background: linear-gradient(135deg, #2A2A2A 0%, #3A3A3A 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  color: #BFBFBF;
  font-size: 14px;
`;

const ProcessContent = styled.div`
  padding: 32px 24px;
`;

const ProcessTitle = styled.h3`
  font-size: 24px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 16px;
`;

const ProcessText = styled.p`
  font-size: 16px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
`;

// Галерея интерфейсов
const GalleryGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 40px;
  margin-top: 40px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 32px;
  }
`;

const GalleryItem = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  overflow: hidden;
`;

const GalleryImage = styled.div`
  width: 100%;
  height: 300px;
  background: linear-gradient(135deg, #2A2A2A 0%, #3A3A3A 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  color: #BFBFBF;
  font-size: 14px;
`;

const GalleryCaption = styled.p`
  padding: 16px;
  font-size: 14px;
  color: #BFBFBF;
  font-weight: 300;
  line-height: 1.4;
  margin: 0;
`;

// Достижения
const AchievementsSection = styled.div`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  padding: 48px;
  text-align: center;
  margin-top: 48px;
  
  @media (max-width: 768px) {
    padding: 32px 24px;
  }
`;

const AchievementsList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 32px 0;
  text-align: left;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const AchievementItem = styled.li`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 16px;
  padding-left: 24px;
  position: relative;
  
  &:before {
    content: '✓';
    position: absolute;
    left: 0;
    color: #3A5AFF;
    font-weight: 600;
  }
`;

const RequestButton = styled(motion.button)`
  display: inline-block;
  padding: 16px 32px;
  background: #3A5AFF;
  color: #FFFFFF;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;
  border: 1px solid #3A5AFF;
  cursor: pointer;
  font-family: inherit;
  margin-top: 24px;
  
  &:hover {
    background: transparent;
    color: #3A5AFF;
  }
`;

// Заключение и призыв
const ConclusionSection = styled.section`
  padding: 120px 0;
  background-color: transparent;
  text-align: center;
`;

const ConclusionTitle = styled(motion.h2)`
  font-size: 40px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 24px;
  letter-spacing: -0.01em;
  
  @media (max-width: 768px) {
    font-size: 32px;
  }
`;

const ConclusionText = styled(motion.p)`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 40px;
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;
`;

const ConclusionCTA = styled(motion.button)`
  display: inline-block;
  padding: 16px 32px;
  background: #FFFFFF;
  color: #151515;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;
  border: 1px solid #FFFFFF;
  cursor: pointer;
  font-family: inherit;
  margin-bottom: 40px;
  
  &:hover {
    background: transparent;
    color: #FFFFFF;
  }
`;

const NavigationLinks = styled(motion.div)`
  display: flex;
  justify-content: center;
  gap: 40px;
  
  @media (max-width: 768px) {
    flex-direction: column;
    gap: 20px;
  }
`;

const NavLink = styled(Link)`
  color: #E0E0E0;
  font-size: 16px;
  font-weight: 400;
  text-decoration: none;
  transition: color 0.2s ease;
  
  &:hover {
    color: #FFFFFF;
  }
`;

// Анимационные варианты
const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1,
      delayChildren: 0.2
    }
  }
};

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: {
    opacity: 1,
    y: 0,
    transition: { duration: 0.6, ease: "easeOut" }
  }
};

const MoyaAptechkaPage = () => {
  const { openPopup } = useContactPopupContext();

  const handleContactClick = () => {
    openPopup();
  };

  const handleRequestResults = () => {
    openPopup();
  };

  return (
    <ProjectPageContainer>
      <main>
        {/* Проблема (вступление) */}
        <ProblemSection>
          <ProblemBackground />
          <MinimalHeader>
            <HeaderContainer>
              <Logo to="/">CORE</Logo>
              <BackButton to="/projects">
                ← Все проекты
              </BackButton>
            </HeaderContainer>
          </MinimalHeader>
          <Container>
            <ProblemContent>
              <ProblemText>
                <Title
                  initial={{ opacity: 0, y: 30 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.8, delay: 0.2 }}
                >
                  Моя Аптечка &mdash; мобильное приложение
                </Title>
                
                <ProblemDescription
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: 0.4 }}
                >
                  Большинство людей теряют контроль над домашними лекарствами: просроченные препараты, отсутствие инструкции под рукой, дубли при покупках. Мы вместе с заказчиком создали мобильное приложение, которое решает эти проблемы и помогает управлять аптечкой ответственно
                </ProblemDescription>
                
                <HeroButtons
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: 0.6 }}
                >
                  <HeroButton
                    onClick={() => window.open('https://my-kit.ru', '_blank')}
                    whileHover={{ scale: 1.02 }}
                  >
                    Посетить посадочную страницу проекта
                  </HeroButton>
                  <PrimaryHeroButton
                    onClick={handleContactClick}
                    whileHover={{ scale: 1.02 }}
                  >
                    Заказать консультацию по проекту
                  </PrimaryHeroButton>
                </HeroButtons>
              </ProblemText>
              
              <ProblemImage
                initial={{ opacity: 0, x: 30 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.8, delay: 0.4 }}
              >
                <img 
                  src="/images/my-kit/hero.png" 
                  alt="Моя Аптечка - мобильное приложение"
                />
              </ProblemImage>
            </ProblemContent>
          </Container>
        </ProblemSection>

        {/* Ключевые функции */}
        <Section>
          <SectionContainer>
            <SectionTitle>Что умеет «Моя Аптечка»</SectionTitle>
            <FunctionsGrid
              as={motion.div}
              variants={containerVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              <FunctionCard variants={itemVariants}>
                <FunctionIcon>📱</FunctionIcon>
                <FunctionTitle>Учет лекарств</FunctionTitle>
                <FunctionDescription>
                  Учет всех лекарств с фото и сроками годности
                </FunctionDescription>
              </FunctionCard>
              
              <FunctionCard variants={itemVariants}>
                <FunctionIcon>🔔</FunctionIcon>
                <FunctionTitle>Уведомления</FunctionTitle>
                <FunctionDescription>
                  Уведомления о приеме препаратов
                </FunctionDescription>
              </FunctionCard>
              
              <FunctionCard variants={itemVariants}>
                <FunctionIcon>🔍</FunctionIcon>
                <FunctionTitle>Поиск</FunctionTitle>
                <FunctionDescription>
                  Поиск по названию и инструкции
                </FunctionDescription>
              </FunctionCard>
              
              <FunctionCard variants={itemVariants}>
                <FunctionIcon>👨‍👩‍👧‍👦</FunctionIcon>
                <FunctionTitle>Семейный доступ</FunctionTitle>
                <FunctionDescription>
                  Контроль за лекарствами членов семьи
                </FunctionDescription>
              </FunctionCard>
              
              <FunctionCard variants={itemVariants}>
                <FunctionIcon>✅</FunctionIcon>
                <FunctionTitle>Курсы лечения</FunctionTitle>
                <FunctionDescription>
                  Создание и следование курсов лечения
                </FunctionDescription>
              </FunctionCard>
              
              <FunctionCard variants={itemVariants}>
                <FunctionIcon>☁️</FunctionIcon>
                <FunctionTitle>Синхронизация</FunctionTitle>
                <FunctionDescription>
                  Синхронизация между устройствами
                </FunctionDescription>
              </FunctionCard>
            </FunctionsGrid>
          </SectionContainer>
        </Section>

        {/* Процесс работы */}
        <Section>
          <SectionContainer>
            <SectionTitle>Как CORE разрабатывает медтех-проекты</SectionTitle>
            <ProcessGrid>
              <ProcessCard
                as={motion.div}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6 }}
                viewport={{ once: true }}
              >
                <ProcessContent>
                  <ProcessTitle>Исследование и аналитика</ProcessTitle>
                  <ProcessText>
                    Анализ лучших приложений в сфере eHealth. Определение регуляторных требований 152-ФЗ. Составление CJM пользователей (пациенты, семьи, врачи, клиники).
                  </ProcessText>
                </ProcessContent>
              </ProcessCard>
              
              <ProcessCard
                as={motion.div}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.1 }}
                viewport={{ once: true }}
              >
                <ProcessContent>
                  <ProcessTitle>Прототипирование и дизайн</ProcessTitle>
                  <ProcessText>
                    UX-прототипы (добавление лекарства за 2 клика). UI-дизайн в спокойных, доверительных цветах. Тестирование на фокус-группе студентов-медиков. Консультации с врачами.
                  </ProcessText>
                </ProcessContent>
              </ProcessCard>
              
              <ProcessCard
                as={motion.div}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.2 }}
                viewport={{ once: true }}
              >
                <ProcessContent>
                  <ProcessTitle>Разработка</ProcessTitle>
                  <ProcessText>
                    Кроссплатформенная или нативная архитектура (iOS, Android). Система напоминаний (push-уведомления). Локальное хранение данных с шифрованием.
                  </ProcessText>
                </ProcessContent>
              </ProcessCard>
              
              <ProcessCard
                as={motion.div}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.3 }}
                viewport={{ once: true }}
              >
                <ProcessContent>
                  <ProcessTitle>Тестирование и публикация</ProcessTitle>
                  <ProcessText>
                    Многоуровневое тестирование (unit, интеграционные, UX). Публикация в 4 маркетплейсах (App Store, Google Play, Huawei AppGallery, RuStore). Настройка мониторинга crash-логов.
                  </ProcessText>
                </ProcessContent>
              </ProcessCard>
            </ProcessGrid>
          </SectionContainer>
        </Section>

        {/* Галерея интерфейсов */}
        <Section>
          <SectionContainer>
            <SectionTitle
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
            >
              Интерфейсы «Моей Аптечки»
            </SectionTitle>
            <GalleryGrid
              as={motion.div}
              variants={containerVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              <GalleryItem variants={itemVariants}>
                <GalleryImage>Главный экран</GalleryImage>
                <GalleryCaption>
                  Удобный список препаратов
                </GalleryCaption>
              </GalleryItem>
              
              <GalleryItem variants={itemVariants}>
                <GalleryImage>Карточка лекарства</GalleryImage>
                <GalleryCaption>
                  Фото, инструкция, срок годности
                </GalleryCaption>
              </GalleryItem>
              
              <GalleryItem variants={itemVariants}>
                <GalleryImage>Уведомления</GalleryImage>
                <GalleryCaption>
                  Напоминания о приёме
                </GalleryCaption>
              </GalleryItem>
              
              <GalleryItem variants={itemVariants}>
                <GalleryImage>Семейный доступ</GalleryImage>
                <GalleryCaption>
                  Управление семейной аптечкой
                </GalleryCaption>
              </GalleryItem>
            </GalleryGrid>
          </SectionContainer>
        </Section>

        {/* Достижения */}
        <Section>
          <SectionContainer>
            <SectionTitle>Результаты проекта</SectionTitle>
            <AchievementsSection>
              <AchievementsList>
                <AchievementItem>Опубликовано в 4 интернет-магазинах</AchievementItem>
                <AchievementItem>Более X скачиваний (данные можно указать по согласию клиента)</AchievementItem>
                <AchievementItem>Приложение используется семьями и студентами-медиками</AchievementItem>
                <AchievementItem>Получены положительные отзывы о простоте и удобстве</AchievementItem>
              </AchievementsList>
              <RequestButton
                onClick={handleRequestResults}
                whileHover={{ scale: 1.02 }}
              >
                Обсудить детали проекта
              </RequestButton>
            </AchievementsSection>
          </SectionContainer>
        </Section>

        {/* Заключение и призыв */}
        <ConclusionSection>
          <SectionContainer>
            <ConclusionTitle
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
            >
              Готовы создать медтех-продукт?
            </ConclusionTitle>
            <ConclusionText
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
            >
              Проект «Моя Аптечка» доказал: мы умеем работать с медтехом, где важна не только эстетика, но и безопасность. Мы готовы создавать приложения для здравоохранения и других высокоответственных сфер
            </ConclusionText>
            <ConclusionCTA
              onClick={handleContactClick}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
              whileHover={{ scale: 1.02 }}
            >
              Заказать разработку медтех-продукта
            </ConclusionCTA>
            <NavigationLinks
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
            >
              <NavLink to="/projects">← Все проекты</NavLink>
              <NavLink to="/">Главная</NavLink>
            </NavigationLinks>
          </SectionContainer>
        </ConclusionSection>
      </main>
    </ProjectPageContainer>
  );
};

export default MoyaAptechkaPage;
