import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const ValuesSection = styled.section`
  padding: 120px 0;
  background-color: #151515;
`;

const Container = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 80px;
`;

const Title = styled(motion.h2)`
  font-size: 40px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 24px;
  letter-spacing: -0.01em;
  
  @media (max-width: 768px) {
    font-size: 28px;
  }
`;

const ValuesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 40px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 32px;
  }
`;

const ValueCard = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  padding: 40px;
  text-align: center;
  transition: all 0.3s ease;
  
  &:hover {
    border-color: #BFBFBF;
    transform: translateY(-4px);
  }
`;

const ValueIcon = styled.div`
  width: 60px;
  height: 60px;
  background: #3A5AFF;
  border-radius: 50%;
  margin: 0 auto 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 24px;
  color: #FFFFFF;
`;

const ValueTitle = styled.h3`
  font-size: 24px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 16px;
`;

const ValueDescription = styled.p`
  font-size: 16px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
`;

const Values = () => {
  const values = [
    {
      id: 1,
      icon: '✦',
      title: 'Минимализм',
      description: 'Мы убираем лишнее и оставляем суть. Каждый элемент имеет смысл и работает на цель.'
    },
    {
      id: 2,
      icon: '◆',
      title: 'Точность',
      description: 'В дизайне, коде и сроках. Мы делаем всё с первого раза правильно и в установленные временные рамки.'
    },
    {
      id: 3,
      icon: '○',
      title: 'Прозрачность',
      description: 'Открытая коммуникация и процессы. Вы всегда знаете, на каком этапе находится ваш проект.'
    },
    {
      id: 4,
      icon: '▲',
      title: 'Результат',
      description: 'Мы измеряем успех бизнес-показателями: рост конверсий, увеличение продаж, повышение узнаваемости.'
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <ValuesSection>
      <Container>
        <SectionHeader>
          <Title
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            Наши ценности и подход
          </Title>
        </SectionHeader>

        <ValuesGrid
          as={motion.div}
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
        >
          {values.map((value) => (
            <ValueCard
              key={value.id}
              variants={itemVariants}
              whileHover={{ scale: 1.02 }}
            >
              <ValueIcon>{value.icon}</ValueIcon>
              <ValueTitle>{value.title}</ValueTitle>
              <ValueDescription>{value.description}</ValueDescription>
            </ValueCard>
          ))}
        </ValuesGrid>
      </Container>
    </ValuesSection>
  );
};

export default Values;
