import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { useContactPopupContext } from '../../contexts/ContactPopupContext';

// Container
const ProjectPageContainer = styled.div`
  background-color: #151515;
  min-height: 100vh;
`;

// Minimal Header
const MinimalHeader = styled.header`
  padding: 24px 0;
  background-color: #151515;
  border-bottom: 1px solid #2A2A2A;
`;

const HeaderContainer = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const Logo = styled(Link)`
  font-family: 'Neue Haas Grotesk Display', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  font-size: 24px;
  font-weight: 700;
  color: #FFFFFF;
  text-decoration: none;
  transition: color 0.2s ease;
  
  &:hover {
    color: #3A5AFF;
  }
`;

const BackButton = styled(Link)`
  display: flex;
  align-items: center;
  gap: 8px;
  color: #E0E0E0;
  font-size: 16px;
  font-weight: 400;
  text-decoration: none;
  transition: color 0.2s ease;
  
  &:hover {
    color: #FFFFFF;
  }
`;

// Hero компонент
const HeroSection = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  padding: 120px 0 80px;
  position: relative;
  overflow: hidden;
  background: linear-gradient(135deg, rgba(21, 21, 21, 0.8) 0%, rgba(42, 42, 42, 0.6) 100%);
`;

const HeroBackground = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(135deg, rgb(29, 78, 216) 0%, #FFFFFF 100%);
`;

const Container = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  width: 100%;
  position: relative;
  z-index: 2;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const HeroContent = styled.div`
  max-width: 800px;
`;

const Title = styled(motion.h1)`
  font-family: 'Akkurat Pro', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  font-size: 64px;
  font-weight: 700;
  line-height: 1.1;
  margin-bottom: 32px;
  color: #FFFFFF;
  letter-spacing: -0.02em;
  
  @media (max-width: 768px) {
    font-size: 40px;
  }
`;

const Subtitle = styled(motion.p)`
  font-size: 20px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 24px;
  max-width: 600px;
  
  @media (max-width: 768px) {
    font-size: 18px;
  }
`;

const AdditionalText = styled(motion.p)`
  font-size: 16px;
  color: #BFBFBF;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 48px;
  max-width: 600px;
  font-style: italic;
  
  @media (max-width: 768px) {
    font-size: 14px;
    margin-bottom: 40px;
  }
`;

const CTAButton = styled(motion.button)`
  display: inline-block;
  padding: 16px 32px;
  background: #FFFFFF;
  color: #151515;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;
  border: 1px solid #FFFFFF;
  cursor: pointer;
  font-family: inherit;
  
  &:hover {
    background: transparent;
    color: #FFFFFF;
  }
`;

const SecondaryButton = styled(motion.button)`
  display: inline-block;
  padding: 16px 32px;
  background: transparent;
  color: #FFFFFF;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;
  border: 1px solid #FFFFFF;
  cursor: pointer;
  font-family: inherit;
  
  &:hover {
    background: #FFFFFF;
    color: #151515;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 20px;
  
  @media (max-width: 768px) {
    flex-direction: column;
    gap: 16px;
  }
`;

// Основные секции
const Section = styled.section`
  padding: 120px 0;
  background-color: #151515;
`;

const SectionContainer = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const SectionTitle = styled.h2`
  font-family: 'Akkurat Pro', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  font-size: 48px;
  font-weight: 700;
  color: #FFFFFF;
  margin-bottom: 48px;
  letter-spacing: -0.02em;
  
  @media (max-width: 768px) {
    font-size: 32px;
    margin-bottom: 32px;
  }
`;

const SectionText = styled.p`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 24px;
  max-width: 800px;
`;

// Подход CORE - 3 колонки
const ApproachGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 60px;
  margin-top: 48px;
  
  @media (max-width: 1024px) {
    grid-template-columns: 1fr;
    gap: 40px;
  }
`;

const ApproachCard = styled.div`
  text-align: center;
`;

const ApproachImage = styled.div`
  width: 100%;
  height: 200px;
  background: linear-gradient(135deg, #2A2A2A 0%, #3A3A3A 100%);
  border-radius: 8px;
  margin-bottom: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #BFBFBF;
  font-size: 14px;
`;

const ApproachTitle = styled.h3`
  font-size: 24px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 16px;
`;

const ApproachText = styled.p`
  font-size: 16px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
`;

// Timeline
const TimelineContainer = styled.div`
  margin-top: 48px;
  position: relative;
`;

const TimelineItem = styled.div`
  display: flex;
  align-items: flex-start;
  margin-bottom: 40px;
  position: relative;
  
  &:before {
    content: '';
    position: absolute;
    left: 20px;
    top: 40px;
    bottom: -40px;
    width: 2px;
    background: #3A5AFF;
  }
  
  &:last-child:before {
    display: none;
  }
  
  @media (max-width: 768px) {
    flex-direction: column;
    margin-bottom: 32px;
    
    &:before {
      display: none;
    }
  }
`;

const TimelineNumber = styled.div`
  width: 40px;
  height: 40px;
  background: #3A5AFF;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #FFFFFF;
  font-weight: 600;
  font-size: 16px;
  margin-right: 32px;
  flex-shrink: 0;
  position: relative;
  z-index: 2;
  
  @media (max-width: 768px) {
    margin-right: 0;
    margin-bottom: 16px;
  }
`;

const TimelineContent = styled.div`
  flex: 1;
`;

const TimelineTitle = styled.h3`
  font-size: 20px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 8px;
`;

const TimelineDescription = styled.p`
  font-size: 16px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
`;

// Галерея интерфейсов
const GalleryGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 40px;
  margin-top: 40px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 32px;
  }
`;

const GalleryItem = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  overflow: hidden;
`;

const GalleryImage = styled.div`
  width: 100%;
  height: 200px;
  background: linear-gradient(135deg, #2A2A2A 0%, #3A3A3A 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  color: #BFBFBF;
  font-size: 14px;
`;

const GalleryCaption = styled.p`
  padding: 16px;
  font-size: 14px;
  color: #BFBFBF;
  font-weight: 300;
  line-height: 1.4;
  margin: 0;
`;

// Результаты по запросу
const ResultsSection = styled.div`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  padding: 48px;
  text-align: center;
  margin-top: 48px;
  
  @media (max-width: 768px) {
    padding: 32px 24px;
  }
`;

const ResultsText = styled.p`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 32px;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const RequestButton = styled(motion.button)`
  display: inline-block;
  padding: 16px 32px;
  background: #3A5AFF;
  color: #FFFFFF;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;
  border: 1px solid #3A5AFF;
  cursor: pointer;
  font-family: inherit;
  
  &:hover {
    background: transparent;
    color: #3A5AFF;
  }
`;

// Контактный блок
const ContactSection = styled.section`
  padding: 120px 0;
  background-color: #1A1A1A;
  text-align: center;
`;

const ContactTitle = styled(motion.h2)`
  font-size: 40px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 24px;
  letter-spacing: -0.01em;
  
  @media (max-width: 768px) {
    font-size: 32px;
  }
`;

const ContactText = styled(motion.p)`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 40px;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const ContactCTA = styled(motion.button)`
  display: inline-block;
  padding: 16px 32px;
  background: #FFFFFF;
  color: #151515;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;
  border: 1px solid #FFFFFF;
  cursor: pointer;
  font-family: inherit;
  margin-bottom: 40px;
  
  &:hover {
    background: transparent;
    color: #FFFFFF;
  }
`;

const NavigationLinks = styled(motion.div)`
  display: flex;
  justify-content: center;
  gap: 40px;
  
  @media (max-width: 768px) {
    flex-direction: column;
    gap: 20px;
  }
`;

const NavLink = styled(Link)`
  color: #E0E0E0;
  font-size: 16px;
  font-weight: 400;
  text-decoration: none;
  transition: color 0.2s ease;
  
  &:hover {
    color: #FFFFFF;
  }
`;

// Анимационные варианты
const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1,
      delayChildren: 0.2
    }
  }
};

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: {
    opacity: 1,
    y: 0,
    transition: { duration: 0.6, ease: "easeOut" }
  }
};

const SpectraCpqPage = () => {
  const { openPopup } = useContactPopupContext();

  const handleContactClick = () => {
    openPopup();
  };

  const handleRequestResults = () => {
    openPopup();
  };

  return (
    <ProjectPageContainer>
      <MinimalHeader>
        <HeaderContainer>
          <Logo to="/">CORE</Logo>
          <BackButton to="/projects">
            ← Все проекты
          </BackButton>
        </HeaderContainer>
      </MinimalHeader>

      <main>
        {/* Hero Section */}
        <HeroSection>
          <HeroBackground />
          <Container>
            <HeroContent>
              <Title
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.2 }}
              >
                CPQ-система «Спектра»
              </Title>
              
              <Subtitle
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.4 }}
              >
                Комплексное решение для автоматизации коммерческих предложений и производственных заказов.
              </Subtitle>
              
              <AdditionalText
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.5 }}
              >
                В этом кейсе мы раскрываем наш подход к аналитике, проектированию и согласованию, но без публикации конфиденциальных бизнес-результатов.
              </AdditionalText>
              
              <ButtonGroup
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.6 }}
              >
                <CTAButton
                  onClick={handleContactClick}
                  whileHover={{ scale: 1.02 }}
                >
                  Обсудить проект
                </CTAButton>
                
                <SecondaryButton
                  as="a"
                  href="https://spectra-cpq.ru"
                  target="_blank"
                  rel="noopener noreferrer"
                  whileHover={{ scale: 1.02 }}
                >
                  Перейти на посадочную страницу
                </SecondaryButton>
              </ButtonGroup>
            </HeroContent>
          </Container>
        </HeroSection>

        {/* Контекст проекта */}
        <Section>
          <SectionContainer>
            <SectionTitle>О проекте</SectionTitle>
            <SectionText>
              «Спектра» — специализированная CPQ-система (Configure — Price — Quote), разработанная для B2B-сегмента. Она позволяет компаниям быстро собирать коммерческие предложения, рассчитывать стоимость и формировать заказы в единой системе.
            </SectionText>
            <SectionText>
              Перед нашей командой стояла задача — создать систему, которая станет ядром цифрового процесса заказчика.
            </SectionText>
          </SectionContainer>
        </Section>

        {/* Подход CORE */}
        <Section style={{ backgroundColor: '#1A1A1A' }}>
          <SectionContainer>
            <SectionTitle>Как мы подходим к работе над сложными системами</SectionTitle>
            <ApproachGrid>
              <ApproachCard>
                <ApproachImage>Аналитика</ApproachImage>
                <ApproachTitle>Аналитика</ApproachTitle>
                <ApproachText>
                  Провели интервью с ключевыми сотрудниками заказчика. Разобрали существующие бизнес-процессы. Построили CJM (карта взаимодействия ролей и документов). Зафиксировали боли и точки роста.
                </ApproachText>
              </ApproachCard>
              
              <ApproachCard>
                <ApproachImage>Проектирование и дизайн</ApproachImage>
                <ApproachTitle>Проектирование и дизайн</ApproachTitle>
                <ApproachText>
                  Составили прототипы интерфейсов. Проработали сценарии работы с заказами, прайсами и ролями. Разработали систему UI-компонентов.
                </ApproachText>
              </ApproachCard>
              
              <ApproachCard>
                <ApproachImage>Разработка и согласование</ApproachImage>
                <ApproachTitle>Разработка и согласование</ApproachTitle>
                <ApproachText>
                  Итеративная разработка на спринтах. Постоянные демо для заказчика. Быстрые правки по обратной связи.
                </ApproachText>
              </ApproachCard>
            </ApproachGrid>
          </SectionContainer>
        </Section>

        {/* Этапы работы */}
        <Section>
          <SectionContainer>
            <SectionTitle>Этапы реализации</SectionTitle>
            <TimelineContainer>
              <TimelineItem>
                <TimelineNumber>01</TimelineNumber>
                <TimelineContent>
                  <TimelineTitle>Анализ и постановка задачи</TimelineTitle>
                  <TimelineDescription>
                    Изучение бизнес-процессов, интервью с заказчиком, формирование технического задания
                  </TimelineDescription>
                </TimelineContent>
              </TimelineItem>
              
              <TimelineItem>
                <TimelineNumber>02</TimelineNumber>
                <TimelineContent>
                  <TimelineTitle>Прототипирование и согласование CJM</TimelineTitle>
                  <TimelineDescription>
                    Создание карты взаимодействия пользователей, прототипов и согласование с заказчиком
                  </TimelineDescription>
                </TimelineContent>
              </TimelineItem>
              
              <TimelineItem>
                <TimelineNumber>03</TimelineNumber>
                <TimelineContent>
                  <TimelineTitle>UI/UX-дизайн и дизайн-система</TimelineTitle>
                  <TimelineDescription>
                    Разработка интерфейсов, создание дизайн-системы и компонентов
                  </TimelineDescription>
                </TimelineContent>
              </TimelineItem>
              
              <TimelineItem>
                <TimelineNumber>04</TimelineNumber>
                <TimelineContent>
                  <TimelineTitle>Разработка MVP</TimelineTitle>
                  <TimelineDescription>
                    Создание минимально жизнеспособного продукта с ключевой функциональностью
                  </TimelineDescription>
                </TimelineContent>
              </TimelineItem>
              
              <TimelineItem>
                <TimelineNumber>05</TimelineNumber>
                <TimelineContent>
                  <TimelineTitle>Итеративное улучшение и интеграции</TimelineTitle>
                  <TimelineDescription>
                    Доработка функциональности, интеграция с внешними системами
                  </TimelineDescription>
                </TimelineContent>
              </TimelineItem>
              
              <TimelineItem>
                <TimelineNumber>06</TimelineNumber>
                <TimelineContent>
                  <TimelineTitle>Поддержка и масштабирование</TimelineTitle>
                  <TimelineDescription>
                    Техническая поддержка, мониторинг, планы развития системы
                  </TimelineDescription>
                </TimelineContent>
              </TimelineItem>
            </TimelineContainer>
          </SectionContainer>
        </Section>

        {/* Галерея интерфейсов */}
        <Section style={{ backgroundColor: '#1A1A1A' }}>
          <SectionContainer>
            <SectionTitle
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
            >
              Интерфейсы системы
            </SectionTitle>
            <GalleryGrid
              as={motion.div}
              variants={containerVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              <GalleryItem variants={itemVariants}>
                <GalleryImage>Dashboard аналитики</GalleryImage>
                <GalleryCaption>
                  Dashboard — аналитика и контроль процессов
                </GalleryCaption>
              </GalleryItem>
              
              <GalleryItem variants={itemVariants}>
                <GalleryImage>Конфигуратор заказов</GalleryImage>
                <GalleryCaption>
                  Конфигуратор заказа — основной инструмент менеджера
                </GalleryCaption>
              </GalleryItem>
              
              <GalleryItem variants={itemVariants}>
                <GalleryImage>Шаблоны предложений</GalleryImage>
                <GalleryCaption>
                  Таблицы с шаблонами коммерческих предложений
                </GalleryCaption>
              </GalleryItem>
              
              <GalleryItem variants={itemVariants}>
                <GalleryImage>Управление ролями</GalleryImage>
                <GalleryCaption>
                  Ролевая модель — гибкое управление доступами
                </GalleryCaption>
              </GalleryItem>
            </GalleryGrid>
          </SectionContainer>
        </Section>

        {/* Результаты по запросу */}
        <Section>
          <SectionContainer>
            <SectionTitle>Результаты внедрения</SectionTitle>
            <ResultsSection>
              <ResultsText>
              CPQ-система «Спектра» уже работает на стороне заказчика. Детали эффективности и показатели скрыты в силу коммерческой тайны, что подтверждает уровень доверия к нам. В личной беседе мы можем поделиться опытом и обсудить, как создать решение сопоставимой сложности для вашей компании.
              </ResultsText>
              <RequestButton
                onClick={handleRequestResults}
                whileHover={{ scale: 1.02 }}
              >
                Запросить консультацию
              </RequestButton>
            </ResultsSection>
          </SectionContainer>
        </Section>

        {/* Контактный блок */}
        <ContactSection>
          <SectionContainer>
            <ContactTitle
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
            >
              Хотите цифровую систему для вашего бизнеса?
            </ContactTitle>
            <ContactText
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
            >
              Мы специализируемся на сложных B2B-решениях: CPQ, ERP, SaaS. Обсудим ваш проект?
            </ContactText>
            <ContactCTA
              onClick={handleContactClick}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
              whileHover={{ scale: 1.02 }}
            >
              Обсудить проект
            </ContactCTA>
            <NavigationLinks
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
            >
              <NavLink to="/projects">← Все проекты</NavLink>
              <NavLink to="/">Главная</NavLink>
            </NavigationLinks>
          </SectionContainer>
        </ContactSection>
      </main>
    </ProjectPageContainer>
  );
};

export default SpectraCpqPage;
