import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const ProjectsSection = styled.section`
  padding: 120px 0;
  background-color: #151515;
`;

const Container = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
`;

const SectionHeader = styled.div`
  text-align: left;
  margin-bottom: 80px;
`;

const Title = styled(motion.h2)`
  font-size: 40px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 24px;
  letter-spacing: -0.01em;
  
  @media (max-width: 768px) {
    font-size: 28px;
  }
`;

const ProjectsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 40px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 32px;
  }
`;

const ProjectCard = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  overflow: hidden;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:hover {
    border-color: #BFBFBF;
    transform: translateY(-4px);
  }
`;

const ProjectImage = styled.div`
  width: 100%;
  height: 280px;
  background: linear-gradient(135deg, #2A2A2A 0%, #3A3A3A 100%);
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, transparent 30%, rgba(191, 191, 191, 0.1) 50%, transparent 70%);
    transform: translateX(-100%);
    transition: transform 0.6s ease;
  }
  
  ${ProjectCard}:hover &::before {
    transform: translateX(100%);
  }
`;

const ProjectOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(21, 21, 21, 0.9);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  opacity: 0;
  transition: opacity 0.3s ease;
  padding: 24px;
  text-align: center;
  
  ${ProjectCard}:hover & {
    opacity: 1;
  }
`;

const OverlayTitle = styled.h3`
  font-size: 24px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 12px;
`;

const OverlayResult = styled.p`
  font-size: 16px;
  color: #BFBFBF;
  font-weight: 400;
`;

const ProjectContent = styled.div`
  padding: 24px;
`;

const ProjectTitle = styled.h3`
  font-size: 24px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 12px;
`;

const ProjectDescription = styled.p`
  font-size: 16px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
`;

const CitiesPromo = styled(motion.div)`
  grid-column: 1 / -1;
  background: #2A2A2A;
  border: 2px solid #3A5AFF;
  padding: 40px;
  text-align: center;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:hover {
    background: #3A5AFF;
    border-color: #3A5AFF;
  }
  
  @media (max-width: 768px) {
    padding: 32px 24px;
  }
`;

const CitiesPromoText = styled.div`
  font-size: 28px;
  font-weight: 600;
  color: #FFFFFF;
  
  @media (max-width: 768px) {
    font-size: 24px;
  }
`;

const ProjectsGridComponent = () => {
  const projects = [
    {
      id: 1,
      title: 'Riviera-Moinako',
      description: 'Сайт жилого комплекса с современным дизайном и удобной навигацией для потенциальных покупателей.',
      result: '+40% обращений инвесторов за 3 месяца',
      image: '/project-riviera.jpg'
    },
    {
      id: 2,
      title: 'Solterra',
      description: 'Брендбук и лендинг для девелоперской компании с уникальной визуальной идентичностью.',
      result: 'Рост узнаваемости бренда на 65%',
      image: '/project-solterra.jpg'
    },
    {
      id: 3,
      title: 'Spectra CPQ',
      description: 'SaaS-сервис для автоматизации процессов производства и управления заказами.',
      result: 'Сокращение времени обработки заказов на 30%',
      image: '/project-spectra.jpg'
    },
    {
      id: 4,
      title: 'CORE Branding',
      description: 'Айдентика и корпоративный сайт для IT-компании с международным присутствием.',
      result: 'Выход на международный рынок',
      image: '/project-core.jpg'
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <ProjectsSection>
      <Container>
        <SectionHeader>
          <Title
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            Избранные проекты
          </Title>
        </SectionHeader>

        <ProjectsGrid
          as={motion.div}
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
        >
          {/* Первые два проекта */}
          {projects.slice(0, 2).map((project) => (
            <ProjectCard
              key={project.id}
              variants={itemVariants}
              whileHover={{ scale: 1.02 }}
              onClick={() => window.location.href = `/project/${project.id}`}
            >
              <ProjectImage>
                <ProjectOverlay>
                  <OverlayTitle>{project.title}</OverlayTitle>
                  <OverlayResult>{project.result}</OverlayResult>
                </ProjectOverlay>
              </ProjectImage>
              <ProjectContent>
                <ProjectTitle>{project.title}</ProjectTitle>
                <ProjectDescription>{project.description}</ProjectDescription>
              </ProjectContent>
            </ProjectCard>
          ))}

          {/* Промо-блок "Смотреть города" */}
          <CitiesPromo
            as={motion.div}
            variants={itemVariants}
            whileHover={{ scale: 1.02 }}
            onClick={() => window.location.href = '/goroda'}
          >
            <CitiesPromoText>Смотреть города</CitiesPromoText>
          </CitiesPromo>

          {/* Последние два проекта */}
          {projects.slice(2, 4).map((project) => (
            <ProjectCard
              key={project.id}
              variants={itemVariants}
              whileHover={{ scale: 1.02 }}
              onClick={() => window.location.href = `/project/${project.id}`}
            >
              <ProjectImage>
                <ProjectOverlay>
                  <OverlayTitle>{project.title}</OverlayTitle>
                  <OverlayResult>{project.result}</OverlayResult>
                </ProjectOverlay>
              </ProjectImage>
              <ProjectContent>
                <ProjectTitle>{project.title}</ProjectTitle>
                <ProjectDescription>{project.description}</ProjectDescription>
              </ProjectContent>
            </ProjectCard>
          ))}
        </ProjectsGrid>
      </Container>
    </ProjectsSection>
  );
};

export default ProjectsGridComponent;
