import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const ParallaxSection = styled.section`
  height: 80vh;
  position: relative;
  overflow: hidden;
  background-color: #151515;
  
  @media (max-width: 768px) {
    height: 60vh;
  }
`;

const ParallaxContainer = styled.div`
  position: relative;
  width: 100%;
  height: 120%;
  will-change: transform;
`;

const ParallaxImage = styled(motion.div)`
  position: absolute;
  top: -10%;
  left: 0;
  width: 100%;
  height: 120%;
  background-image: url('/photo_2025.jpg');
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  z-index: 1;
`;

const ParallaxContent = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  z-index: 10;
  max-width: 600px;
  background: rgba(21, 21, 21, 0.7);
  border-radius: 8px;
  padding: 40px;
`;

const ParallaxText = styled(motion.h2)`
  font-size: 48px;
  font-weight: 600;
  color: #FFFFFF;
  line-height: 1.2;
  letter-spacing: -0.01em;
  margin-bottom: 24px;
  text-shadow: 0 2px 10px rgba(0, 0, 0, 0.5);
  
  @media (max-width: 768px) {
    font-size: 32px;
  }
`;

const ParallaxSubtext = styled(motion.p)`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  text-shadow: 0 1px 5px rgba(0, 0, 0, 0.5);
  
  @media (max-width: 768px) {
    font-size: 16px;
  }
`;

const ParallaxBlock = () => {
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <ParallaxSection>
      <ParallaxContainer>
        <ParallaxImage
          style={{
            transform: `translateY(${scrollY * 0.5}px)`
          }}
        />
        {/* Тестовый div для проверки изображения */}
        <div style={{
          position: 'absolute',
          top: '10px',
          left: '10px',
          width: '200px',
          height: '150px',
          backgroundImage: 'url(/photo_2025.jpg)',
          backgroundSize: 'cover',
          border: '2px solid red',
          zIndex: 100
        }} />
      </ParallaxContainer>
      
      <ParallaxContent>
        <ParallaxText
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          Создаем будущее digital-дизайна
        </ParallaxText>
        
        <ParallaxSubtext
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          viewport={{ once: true }}
        >
          Каждый проект — это возможность создать что-то уникальное и значимое. Мы превращаем идеи в визуальные решения, которые работают.
        </ParallaxSubtext>
      </ParallaxContent>
    </ParallaxSection>
  );
};

export default ParallaxBlock;
