import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import FullscreenMenu from '../Menu/FullscreenMenu';

const HeaderContainer = styled.header`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  background-color: ${props => props.$menuOpen ? 'transparent' : (props.$scrolled ? '#151515' : 'transparent')};
  transition: background-color 0.3s ease;
  padding: 24px 0;
`;

const HeaderContent = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const LeftSection = styled.div`
  display: flex;
  align-items: center;
  gap: 40px;
  
  @media (max-width: 768px) {
    gap: 24px;
  }
`;

const Logo = styled(Link)`
  font-size: 28px;
  font-weight: 700;
  color: #FFFFFF;
  letter-spacing: -0.02em;
  text-decoration: none;
  transition: all 0.3s ease;
  ${props => props.$hidden && `
    opacity: 0 !important;
    visibility: hidden !important;
    pointer-events: none !important;
  `}
  
  &:hover {
    color: #BFBFBF;
  }
`;

const MenuButton = styled.button`
  position: relative;
  width: 40px;
  height: 40px;
  background: none;
  border: none;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 10000;
  transition: transform 0.2s ease;
  
  &:hover {
    transform: scale(1.05);
  }
  
  &:active {
    transform: scale(0.95);
  }
`;

const MenuIcon = styled.div`
  position: relative;
  width: 24px;
  height: 24px;
`;

// Атом-кнопка: 8 точек вокруг центральной
const Dot = styled(motion.div)`
  position: absolute;
  width: 4px;
  height: 4px;
  background-color: #FFFFFF;
  border-radius: 50%;
  transform-origin: center;
`;

const CenterDot = styled(motion.div)`
  position: absolute;
  top: 50%;
  left: 50%;
  width: 6px;
  height: 6px;
  background-color: #3A5AFF;
  border-radius: 50%;
  transform: translate(-50%, -50%);
  box-shadow: 0 0 8px rgba(58, 90, 255, 0.6);
`;

// Крестик для закрытия
const CrossLine = styled(motion.div)`
  position: absolute;
  top: 50%;
  left: 50%;
  width: 20px;
  height: 3px;
  background-color: #FFFFFF;
  border-radius: 2px;
  transform-origin: center;
  box-shadow: 0 0 6px rgba(255, 255, 255, 0.5);
  z-index: 10005;
`;

const Navigation = styled.nav`
  display: flex;
  align-items: center;
  gap: 40px;
  transition: all 0.3s ease;
  ${props => props.$hidden && `
    opacity: 0 !important;
    visibility: hidden !important;
    pointer-events: none !important;
  `}
  
  @media (max-width: 768px) {
    display: ${props => props.$scrolled ? 'none' : 'flex'};
    gap: 24px;
  }
  
  @media (max-width: 480px) {
    display: none;
  }
`;

const NavLink = styled(Link)`
  color: #FFFFFF;
  text-decoration: none;
  font-weight: 400;
  font-size: 16px;
  transition: color 0.3s ease;
  position: relative;
  
  &:hover {
    color: #BFBFBF;
  }
  
  ${props => props.$accent && `
    color: #3A5AFF;
    font-weight: 500;
    
    &:hover {
      color: #5A7AFF;
    }
  `}
`;

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Блокировка скролла при открытом меню
  useEffect(() => {
    if (isMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }
    
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [isMenuOpen]);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  // Позиции точек атома (8 точек по кругу)
  const atomPositions = [
    { x: 10, y: 2 },   // верх
    { x: 17, y: 5 },   // верх-право
    { x: 20, y: 12 },  // право
    { x: 17, y: 19 },  // низ-право
    { x: 10, y: 22 },  // низ
    { x: 3, y: 19 },   // низ-лево
    { x: 0, y: 12 },   // лево
    { x: 3, y: 5 }     // верх-лево
  ];

  // Позиции для сетки 3x3 (убираем центральную)
  const gridPositions = [
    { x: 4, y: 4 },    // верх-лево
    { x: 11, y: 4 },   // верх-центр
    { x: 18, y: 4 },   // верх-право
    { x: 4, y: 11 },   // центр-лево
    { x: 18, y: 11 },  // центр-право
    { x: 4, y: 18 },   // низ-лево
    { x: 11, y: 18 },  // низ-центр
    { x: 18, y: 18 }   // низ-право
  ];

  const dotVariants = {
    atom: (i) => ({
      x: atomPositions[i].x,
      y: atomPositions[i].y,
      scale: 1,
      opacity: 0.8,
      transition: { duration: 0.4, ease: "easeOut" }
    }),
    grid: (i) => ({
      x: gridPositions[i].x,
      y: gridPositions[i].y,
      scale: 1,
      opacity: 1,
      transition: { duration: 0.4, ease: "easeOut", delay: i * 0.02 }
    }),
    cross: {
      scale: 0,
      opacity: 0,
      transition: { duration: 0.2 }
    }
  };

  const centerDotVariants = {
    atom: {
      scale: 1,
      opacity: 1,
      transition: { duration: 0.4 }
    },
    grid: {
      scale: 0,
      opacity: 0,
      transition: { duration: 0.2 }
    },
    cross: {
      scale: 0,
      opacity: 0,
      transition: { duration: 0.2 }
    }
  };

  const crossVariants = {
    atom: {
      scale: 0,
      opacity: 0,
      transition: { duration: 0.2 }
    },
    cross: {
      scale: 1,
      opacity: 1,
      transition: { duration: 0.4, delay: 0.2, ease: "easeOut" }
    }
  };

  const crossHoverVariants = {
    hover: {
      scale: 1.1,
      boxShadow: "0 0 8px rgba(255, 255, 255, 0.5)",
      transition: { duration: 0.2 }
    }
  };

  const currentState = isMenuOpen ? 'cross' : 'atom';
  
  // Отладка состояния
  console.log('Header Debug:', { 
    isMenuOpen, 
    currentState, 
    scrolled,
    logoHidden: isMenuOpen,
    navHidden: isMenuOpen 
  });

  return (
    <>
      <HeaderContainer $scrolled={scrolled} $menuOpen={isMenuOpen}>
        <HeaderContent>
          <LeftSection>
            <MenuButton 
              onClick={toggleMenu} 
              aria-label={isMenuOpen ? "Закрыть меню" : "Открыть меню"}
            >
              <MenuIcon>
                {/* 8 точек атома */}
                {atomPositions.map((_, i) => (
                  <Dot
                    key={`dot-${i}`}
                    custom={i}
                    variants={dotVariants}
                    animate={currentState}
                  />
                ))}
                
                {/* Центральная точка */}
                <CenterDot
                  variants={centerDotVariants}
                  animate={currentState}
                />
                
                {/* Крестик */}
                {isMenuOpen && (
                  <>
                    <CrossLine
                      initial={{ scale: 0, opacity: 0 }}
                      animate={{ scale: 1, opacity: 1 }}
                      transition={{ duration: 0.3, delay: 0.1 }}
                      style={{ transform: 'translate(-50%, -50%) rotate(45deg)' }}
                    />
                    <CrossLine
                      initial={{ scale: 0, opacity: 0 }}
                      animate={{ scale: 1, opacity: 1 }}
                      transition={{ duration: 0.3, delay: 0.1 }}
                      style={{ transform: 'translate(-50%, -50%) rotate(-45deg)' }}
                    />
                  </>
                )}
              </MenuIcon>
            </MenuButton>
            <Logo to="/" $hidden={isMenuOpen}>CORE</Logo>
          </LeftSection>
          
          <Navigation $scrolled={scrolled} $hidden={isMenuOpen} aria-label="Основная навигация">
            <NavLink to="/projects">Проекты</NavLink>
            <NavLink to="/services">Услуги</NavLink>
            <NavLink to="/goroda">Города</NavLink>
            <NavLink to="/contact" $accent>Связаться</NavLink>
          </Navigation>
        </HeaderContent>
      </HeaderContainer>

      <FullscreenMenu isOpen={isMenuOpen} onClose={closeMenu} />
    </>
  );
};

export default Header;
