import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useContactPopupContext } from '../../contexts/ContactPopupContext';

const FooterSection = styled.footer`
  background-color: #151515;
  padding: 120px 0 40px;
  border-top: 1px solid #2A2A2A;
`;

const Container = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
`;

const FooterContent = styled.div`
  display: grid;
  grid-template-columns: 1fr 2fr;
  gap: 80px;
  margin-bottom: 80px;
  
  @media (max-width: 1024px) {
    grid-template-columns: 1fr;
    gap: 60px;
  }
`;

const ImageSection = styled.div`
  @media (max-width: 1024px) {
    order: 2;
  }
`;

const FooterImage = styled.div`
  width: 100%;
  height: 400px;
  background: linear-gradient(135deg, #2A2A2A 0%, #3A3A3A 100%);
  border: 1px solid #3A3A3A;
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, transparent 30%, rgba(191, 191, 191, 0.1) 50%, transparent 70%);
    transform: translateX(-100%);
    transition: transform 0.6s ease;
  }
  
  &:hover::before {
    transform: translateX(100%);
  }
  
  @media (max-width: 1024px) {
    height: 300px;
  }
`;

const ContentSection = styled.div`
  @media (max-width: 1024px) {
    order: 1;
  }
`;

const IdeaButton = styled(motion.div)`
  font-size: 48px;
  font-weight: 700;
  color: #FFFFFF;
  margin-bottom: 60px;
  cursor: pointer;
  transition: color 0.3s ease;
  
  &:hover {
    color: #3A5AFF;
  }
  
  @media (max-width: 768px) {
    font-size: 32px;
    margin-bottom: 40px;
  }
`;

const InfoGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 40px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 32px;
  }
`;

const InfoColumn = styled.div``;

const InfoTitle = styled.h4`
  font-size: 18px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 16px;
`;

const InfoList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
`;

const InfoItem = styled.li`
  margin-bottom: 8px;
  
  &:last-child {
    margin-bottom: 0;
  }
`;

const InfoLink = styled.a`
  color: #E0E0E0;
  text-decoration: none;
  font-size: 16px;
  font-weight: 300;
  transition: color 0.3s ease;
  
  &:hover {
    color: #BFBFBF;
  }
`;

const InfoText = styled.div`
  color: #E0E0E0;
  font-size: 16px;
  font-weight: 300;
  line-height: 1.6;
`;

const BottomSection = styled.div`
  margin-top: 2em;
  border-top: 1px solid #2A2A2A;
  padding-top: 40px;
  text-align: center;
`;

const PrivacyLink = styled.a`
  color: #E0E0E0;
  text-decoration: none;
  font-size: 14px;
  font-weight: 300;
  transition: color 0.3s ease;
  
  &:hover {
    color: #BFBFBF;
  }
`;

const Footer = () => {
  const handleIdeaClick = () => {
    // Здесь можно открыть модальное окно с формой
    console.log('Открыть форму "Воплотить идею"');
  };

  return (
    <FooterSection>
      <Container>
        <FooterContent>
          <ImageSection>
            <FooterImage />
          </ImageSection>

          <ContentSection>
            <IdeaButton
              onClick={handleIdeaClick}
              whileHover={{ scale: 1.02 }}
            >
              Воплотить идею
            </IdeaButton>

            <InfoGrid>
              <InfoColumn>
                <InfoTitle>Новости</InfoTitle>
                <InfoList>
                  <InfoItem>
                    <InfoLink href="https://t.me/core_studio" target="_blank" rel="noopener noreferrer">
                      Telegram канал
                    </InfoLink>
                  </InfoItem>
                </InfoList>
              </InfoColumn>

              <InfoColumn>
                <InfoTitle>Адреса</InfoTitle>
                <InfoList>
                  <InfoItem>
                    <InfoLink href="/moscow">Москва</InfoLink>
                  </InfoItem>
                  <InfoItem>
                    <InfoLink href="/crimea">Крым</InfoLink>
                  </InfoItem>
                  <InfoItem>
                    <InfoLink href="/sochi">Сочи</InfoLink>
                  </InfoItem>
                  <InfoItem>
                    <InfoLink href="/volgograd">Волгоград</InfoLink>
                  </InfoItem>
                  <InfoItem>
                    <InfoLink href="/spb">Санкт-Петербург</InfoLink>
                  </InfoItem>
                </InfoList>
              </InfoColumn>

              <InfoColumn>
                <InfoTitle>Связь</InfoTitle>
                <InfoList>
                  <InfoItem>
                    <InfoLink href="mailto:info@core-studio.ru">
                      info@core-studio.ru
                    </InfoLink>
                  </InfoItem>
                  <InfoItem>
                    <InfoLink href="tel:+79991234567">
                      +7 (999) 123-45-67
                    </InfoLink>
                  </InfoItem>
                </InfoList>
              </InfoColumn>

            </InfoGrid>
            
            <BottomSection>
              <PrivacyLink href="/privacy">
                Политика конфиденциальности
              </PrivacyLink>
            </BottomSection>

          </ContentSection>
        </FooterContent>


      </Container>

      {/* Микроразметка Schema.org */}
      <script
        type="application/ld+json"
        dangerouslySetInnerHTML={{
          __html: JSON.stringify({
            "@context": "https://schema.org",
            "@type": "Organization",
            "name": "CORE",
            "description": "Премиальная веб-студия. Разработка сайтов, брендинг, маркетинг и дизайн для бизнеса.",
            "url": "https://core-studio.ru",
            "logo": "https://core-studio.ru/logo.png",
            "contactPoint": {
              "@type": "ContactPoint",
              "telephone": "+7-999-123-45-67",
              "contactType": "customer service",
              "email": "info@core-studio.ru"
            },
            "address": [
              {
                "@type": "PostalAddress",
                "addressLocality": "Москва",
                "addressCountry": "RU"
              },
              {
                "@type": "PostalAddress",
                "addressLocality": "Санкт-Петербург",
                "addressCountry": "RU"
              }
            ],
            "sameAs": [
              "https://t.me/core_studio"
            ]
          })
        }}
      />
    </FooterSection>
  );
};

export default Footer;
