import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const ProjectsSection = styled.section`
  padding: 120px 0;
  background-color: #151515;
`;

const Container = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
`;

const SectionHeader = styled.div`
  text-align: left;
  margin-bottom: 80px;
`;

const Title = styled(motion.h2)`
  font-size: 40px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 24px;
  letter-spacing: -0.01em;
  
  @media (max-width: 768px) {
    font-size: 28px;
  }
`;

const Subtitle = styled(motion.p)`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  max-width: 600px;
  // margin: 0 auto;
  line-height: 1.6;
`;

const ProjectsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 40px;
  margin-bottom: 80px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 32px;
  }
`;

const ProjectCard = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  overflow: hidden;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:hover {
    border-color: #BFBFBF;
    transform: translateY(-4px);
  }
`;

const ProjectImage = styled.div`
  width: 100%;
  height: 280px;
  background: linear-gradient(135deg, #2A2A2A 0%, #3A3A3A 100%);
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, transparent 30%, rgba(191, 191, 191, 0.1) 50%, transparent 70%);
    transform: translateX(-100%);
    transition: transform 0.6s ease;
  }
  
  ${ProjectCard}:hover &::before {
    transform: translateX(100%);
  }
`;

const ProjectContent = styled.div`
  padding: 24px;
`;

const ProjectTitle = styled.h3`
  font-size: 24px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 12px;
`;

const ProjectResult = styled.p`
  font-size: 16px;
  color: #BFBFBF;
  font-weight: 400;
`;

const CitiesButton = styled(motion.div)`
  grid-column: 1 / -1;
  background: #2A2A2A;
  border: 2px solid #3A5AFF;
  padding: 40px;
  text-align: left;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:hover {
    background: #3A5AFF;
    border-color: #3A5AFF;
  }
  
  @media (max-width: 768px) {
    padding: 32px 24px;
  }
`;

const CitiesButtonText = styled.div`
  font-size: 28px;
  font-weight: 600;
  color: #FFFFFF;
  
  @media (max-width: 768px) {
    font-size: 24px;
  }
`;

const AllProjectsButton = styled(motion.a)`
  display: block;
  width: 66.66%;
  max-width: 400px;
  margin: 0 auto;
  background: #FFFFFF;
  color: #151515;
  text-align: center;
  padding: 20px 40px;
  font-size: 18px;
  font-weight: 600;
  text-decoration: none;
  border: 2px solid #FFFFFF;
  transition: all 0.3s ease;
  
  &:hover {
    background: transparent;
    color: #FFFFFF;
  }
  
  @media (max-width: 768px) {
    width: 100%;
    max-width: none;
  }
`;

const Projects = () => {
  const projects = [
    {
      id: 1,
      title: 'E-commerce платформа',
      result: '+42% заявок / рост продаж на 120%',
      image: '/project1.jpg'
    },
    {
      id: 2,
      title: 'Корпоративный сайт',
      result: '+67% конверсии / увеличение трафика в 3 раза',
      image: '/project2.jpg'
    },
    {
      id: 3,
      title: 'Мобильное приложение',
      result: '+89% удержания / 4.8★ в App Store',
      image: '/project3.jpg'
    },
    {
      id: 4,
      title: 'Лендинг-страница',
      result: '+156% лидов / ROI 340%',
      image: '/project4.jpg'
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <ProjectsSection id="projects">
      <Container>
        <SectionHeader>
          <Title
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            Проекты
          </Title>
          <Subtitle
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            Показываем результат, а не картинки. Каждый кейс — это измеримый рост бизнеса клиентов.
          </Subtitle>
        </SectionHeader>

        <ProjectsGrid
          as={motion.div}
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
        >
          {/* Первые два проекта */}
          {projects.slice(0, 2).map((project) => (
            <ProjectCard
              key={project.id}
              variants={itemVariants}
              whileHover={{ scale: 1.02 }}
            >
              <ProjectImage />
              <ProjectContent>
                <ProjectTitle>{project.title}</ProjectTitle>
                <ProjectResult>{project.result}</ProjectResult>
              </ProjectContent>
            </ProjectCard>
          ))}

          {/* Кнопка "Смотреть города" */}
          <CitiesButton
            as={motion.div}
            variants={itemVariants}
            whileHover={{ scale: 1.02 }}
            onClick={() => window.location.href = '/goroda'}
          >
            <CitiesButtonText>Смотреть города</CitiesButtonText>
          </CitiesButton>

          {/* Последние два проекта */}
          {projects.slice(2, 4).map((project) => (
            <ProjectCard
              key={project.id}
              variants={itemVariants}
              whileHover={{ scale: 1.02 }}
            >
              <ProjectImage />
              <ProjectContent>
                <ProjectTitle>{project.title}</ProjectTitle>
                <ProjectResult>{project.result}</ProjectResult>
              </ProjectContent>
            </ProjectCard>
          ))}
        </ProjectsGrid>

        <AllProjectsButton
          href="/projects"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.4 }}
          viewport={{ once: true }}
          whileHover={{ scale: 1.02 }}
        >
          Все проекты
        </AllProjectsButton>
      </Container>
    </ProjectsSection>
  );
};

export default Projects;
