import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const CTASection = styled.section`
  padding: 120px 0;
  background: radial-gradient(circle at center, #2A2A2A 0%, #151515 100%);
`;

const Container = styled.div`
  max-width: 1440px;
  margin: 0 auto;
  padding: 0 40px;
  
  @media (max-width: 768px) {
    padding: 0 24px;
  }
`;

const CTAContent = styled.div`
  text-align: center;
  max-width: 600px;
  margin: 0 auto;
`;

const CTATitle = styled(motion.h2)`
  font-size: 48px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 24px;
  line-height: 1.2;
  
  @media (max-width: 768px) {
    font-size: 36px;
  }
`;

const CTASubtitle = styled(motion.p)`
  font-size: 20px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 40px;
  
  @media (max-width: 768px) {
    font-size: 18px;
    margin-bottom: 32px;
  }
`;

const CTAButton = styled(motion.a)`
  display: inline-block;
  padding: 20px 40px;
  background: #3A5AFF;
  color: #FFFFFF;
  font-size: 18px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;
  border: 1px solid #3A5AFF;
  
  &:hover {
    background: #5A7AFF;
    border-color: #5A7AFF;
    transform: translateY(-2px);
  }
`;

const CallToAction = () => {
  return (
    <CTASection>
      <Container>
        <CTAContent>
          <CTATitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            Готовы обсудить проект?
          </CTATitle>
          
          <CTASubtitle
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            Напишите нам — и мы предложим решение под ваши цели.
          </CTASubtitle>
          
          <CTAButton
            href="/contact"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
            viewport={{ once: true }}
            whileHover={{ scale: 1.05 }}
          >
            Связаться
          </CTAButton>
        </CTAContent>
      </Container>
    </CTASection>
  );
};

export default CallToAction;
