  font-size: 48px;
  font-weight: 700;
  color: #FFFFFF;
  margin-bottom: 48px;
  letter-spacing: -0.02em;
  
  @media (max-width: 768px) {
    font-size: 32px;
    margin-bottom: 32px;
  }
`;

const SectionText = styled.p`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 24px;
  max-width: 800px;
`;

// Решение - 3 колонки
const SolutionGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 60px;
  margin-top: 48px;
  
  @media (max-width: 1024px) {
