  font-size: 16px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
`;

// Ключевые функции - сетка 3x2
const FunctionsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 40px;
  margin-top: 48px;
  
  @media (max-width: 1024px) {
    grid-template-columns: repeat(2, 1fr);
    gap: 32px;
  }
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 24px;
  }
`;

const FunctionCard = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  padding: 32px 24px;
  text-align: center;
  transition: all 0.3s ease;
  
  &:hover {
    border-color: #3A5AFF;
    transform: translateY(-4px);
  }
`;

const FunctionIcon = styled.div`
  width: 60px;
  height: 60px;
  background: #3A5AFF;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 20px;
  color: #FFFFFF;
  font-size: 24px;
`;

const FunctionTitle = styled.h3`
  font-size: 18px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 12px;
`;

const FunctionDescription = styled.p`
  font-size: 14px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.5;
`;

// Процесс работы - карточки
const ProcessGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 60px;
  margin-top: 48px;
  
  @media (max-width: 1024px) {
    grid-template-columns: 1fr;
    gap: 40px;
  }
`;

const ProcessCard = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  overflow: hidden;
`;

const ProcessImage = styled.div`
  width: 100%;
  height: 200px;
  background: linear-gradient(135deg, #2A2A2A 0%, #3A3A3A 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  color: #BFBFBF;
  font-size: 14px;
`;

const ProcessContent = styled.div`
  padding: 32px 24px;
`;

const ProcessTitle = styled.h3`
  font-size: 24px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 16px;
`;

const ProcessText = styled.p`
  font-size: 16px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
`;

// Галерея интерфейсов
const GalleryGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 40px;
  margin-top: 40px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 32px;
  }
`;

const GalleryItem = styled(motion.div)`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  overflow: hidden;
`;

const GalleryImage = styled.div`
  width: 100%;
  height: 300px;
  background: linear-gradient(135deg, #2A2A2A 0%, #3A3A3A 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  color: #BFBFBF;
  font-size: 14px;
`;

const GalleryCaption = styled.p`
  padding: 16px;
  font-size: 14px;
  color: #BFBFBF;
  font-weight: 300;
  line-height: 1.4;
  margin: 0;
`;

// Достижения
const AchievementsSection = styled.div`
  background: #2A2A2A;
  border: 1px solid #3A3A3A;
  border-radius: 8px;
  padding: 48px;
  text-align: center;
  margin-top: 48px;
  
  @media (max-width: 768px) {
    padding: 32px 24px;
  }
`;

const AchievementsList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 32px 0;
  text-align: left;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const AchievementItem = styled.li`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 16px;
  padding-left: 24px;
  position: relative;
  
  &:before {
    content: '✓';
    position: absolute;
    left: 0;
    color: #3A5AFF;
    font-weight: 600;
  }
`;

const RequestButton = styled(motion.button)`
  display: inline-block;
  padding: 16px 32px;
  background: #3A5AFF;
  color: #FFFFFF;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;
  border: 1px solid #3A5AFF;
  cursor: pointer;
  font-family: inherit;
  margin-top: 24px;
  
  &:hover {
    background: transparent;
    color: #3A5AFF;
  }
`;

// Заключение и призыв
const ConclusionSection = styled.section`
  padding: 120px 0;
  background-color: #1A1A1A;
  text-align: center;
`;

const ConclusionTitle = styled(motion.h2)`
  font-size: 40px;
  font-weight: 600;
  color: #FFFFFF;
  margin-bottom: 24px;
  letter-spacing: -0.01em;
  
  @media (max-width: 768px) {
    font-size: 32px;
  }
`;

const ConclusionText = styled(motion.p)`
  font-size: 18px;
  color: #E0E0E0;
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 40px;
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;
`;

const ConclusionCTA = styled(motion.button)`
  display: inline-block;
  padding: 16px 32px;
  background: #FFFFFF;
  color: #151515;
  font-size: 16px;
  font-weight: 500;
  text-decoration: none;
  transition: all 0.3s ease;
  border: 1px solid #FFFFFF;
  cursor: pointer;
  font-family: inherit;
  margin-bottom: 40px;
  
  &:hover {
    background: transparent;
    color: #FFFFFF;
  }
`;

const NavigationLinks = styled(motion.div)`
  display: flex;
  justify-content: center;
  gap: 40px;
  
  @media (max-width: 768px) {
    flex-direction: column;
    gap: 20px;
  }
`;

const NavLink = styled(Link)`
  color: #E0E0E0;
  font-size: 16px;
  font-weight: 400;
  text-decoration: none;
  transition: color 0.2s ease;
  
  &:hover {
    color: #FFFFFF;
  }
`;

// Анимационные варианты
const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1,
      delayChildren: 0.2
    }
  }
};

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: {
    opacity: 1,
    y: 0,
    transition: { duration: 0.6, ease: "easeOut" }
  }
};

const MoyaAptechkaPage = () => {
  const { openPopup } = useContactPopupContext();

  const handleContactClick = () => {
    openPopup();
  };

  const handleRequestResults = () => {
    openPopup();
  };

  return (
    <ProjectPageContainer>
      <MinimalHeader>
        <HeaderContainer>
          <Logo to="/">CORE</Logo>
          <BackButton to="/projects">
            ← Все проекты
          </BackButton>
        </HeaderContainer>
      </MinimalHeader>

      <main>
        {/* Проблема (вступление) */}
        <ProblemSection>
          <ProblemBackground />
          <Container>
            <ProblemContent>
              <Title
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.2 }}
              >
                Зачем нужна «Моя Аптечка»?
              </Title>
              
              <ProblemText
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.4 }}
              >
                Большинство людей теряют контроль над домашними лекарствами: просроченные препараты, отсутствие инструкции под рукой, дубли при покупках. Мы вместе с заказчиком создали мобильное приложение, которое решает эти проблемы и помогает управлять аптечкой ответственно.
              </ProblemText>
            </ProblemContent>
          </Container>
        </ProblemSection>

        {/* Решение */}
        <Section>
          <SectionContainer>
            <SectionTitle>Как мы подошли к задаче</SectionTitle>
            <SolutionGrid>
              <SolutionCard>
                <SolutionImage>Полноценный сервис</SolutionImage>
                <SolutionTitle>Полноценный сервис</SolutionTitle>
                <SolutionText>
                  Мы проработали продукт как полноценный сервис, а не просто список таблеток.
                </SolutionText>
              </SolutionCard>
              
              <SolutionCard>
                <SolutionImage>Удобство UX</SolutionImage>
                <SolutionTitle>Удобство UX</SolutionTitle>
                <SolutionText>
                  Сфокусировались на удобстве UX (быстрое добавление лекарства, напоминания, поиск).
                </SolutionText>
              </SolutionCard>
              
              <SolutionCard>
                <SolutionImage>Безопасность</SolutionImage>
                <SolutionTitle>Безопасность и стабильность</SolutionTitle>
                <SolutionText>
                  Обеспечили безопасность и стабильность — ведь речь о здоровье.
                </SolutionText>
              </SolutionCard>
            </SolutionGrid>
          </SectionContainer>
        </Section>

        {/* Ключевые функции */}
        <Section style={{ backgroundColor: '#1A1A1A' }}>
          <SectionContainer>
            <SectionTitle>Что умеет «Моя Аптечка»</SectionTitle>
            <FunctionsGrid
              as={motion.div}
              variants={containerVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              <FunctionCard variants={itemVariants}>
                <FunctionIcon>📱</FunctionIcon>
                <FunctionTitle>Учет лекарств</FunctionTitle>
                <FunctionDescription>
                  Учет всех лекарств с фото и сроками годности
                </FunctionDescription>
              </FunctionCard>
              
              <FunctionCard variants={itemVariants}>
                <FunctionIcon>🔔</FunctionIcon>
                <FunctionTitle>Уведомления</FunctionTitle>
                <FunctionDescription>
                  Уведомления о приеме препаратов
                </FunctionDescription>
              </FunctionCard>
              
              <FunctionCard variants={itemVariants}>
                <FunctionIcon>🔍</FunctionIcon>
                <FunctionTitle>Поиск</FunctionTitle>
                <FunctionDescription>
                  Поиск по названию и инструкции
                </FunctionDescription>
              </FunctionCard>
              
              <FunctionCard variants={itemVariants}>
                <FunctionIcon>👨‍👩‍👧‍👦</FunctionIcon>
                <FunctionTitle>Семейный доступ</FunctionTitle>
                <FunctionDescription>
                  Деление аптечки между членами семьи
                </FunctionDescription>
              </FunctionCard>
              
              <FunctionCard variants={itemVariants}>
                <FunctionIcon>📊</FunctionIcon>
                <FunctionTitle>История</FunctionTitle>
                <FunctionDescription>
                  История покупок и расходов
                </FunctionDescription>
              </FunctionCard>
              
              <FunctionCard variants={itemVariants}>
                <FunctionIcon>☁️</FunctionIcon>
                <FunctionTitle>Синхронизация</FunctionTitle>
                <FunctionDescription>
                  Синхронизация между устройствами
                </FunctionDescription>
              </FunctionCard>
            </FunctionsGrid>
          </SectionContainer>
        </Section>

        {/* Процесс работы */}
        <Section>
          <SectionContainer>
            <SectionTitle>Как CORE разрабатывает медтех-проекты</SectionTitle>
            <ProcessGrid>
              <ProcessCard
                as={motion.div}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6 }}
                viewport={{ once: true }}
              >
                <ProcessImage>Исследование и аналитика</ProcessImage>
                <ProcessContent>
                  <ProcessTitle>Исследование и аналитика</ProcessTitle>
                  <ProcessText>
                    Анализ лучших приложений в сфере eHealth. Определение регуляторных требований (GDPR/152-ФЗ). Составление CJM пользователей (пациенты, семьи).
                  </ProcessText>
                </ProcessContent>
              </ProcessCard>
              
              <ProcessCard
                as={motion.div}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.1 }}
                viewport={{ once: true }}
              >
                <ProcessImage>Прототипирование и дизайн</ProcessImage>
                <ProcessContent>
                  <ProcessTitle>Прототипирование и дизайн</ProcessTitle>
                  <ProcessText>
                    UX-прототипы (добавление лекарства за 2 клика). UI-дизайн в спокойных, доверительных цветах. Тестирование на фокус-группе студентов-медиков.
                  </ProcessText>
                </ProcessContent>
              </ProcessCard>
              
              <ProcessCard
                as={motion.div}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.2 }}
                viewport={{ once: true }}
              >
                <ProcessImage>Разработка</ProcessImage>
                <ProcessContent>
                  <ProcessTitle>Разработка</ProcessTitle>
                  <ProcessText>
                    Кроссплатформенная архитектура (iOS, Android, HarmonyOS, AppGallery). Система напоминаний (push-уведомления). Локальное хранение данных с шифрованием.
                  </ProcessText>
                </ProcessContent>
              </ProcessCard>
              
              <ProcessCard
                as={motion.div}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.3 }}
                viewport={{ once: true }}
              >
                <ProcessImage>Тестирование и публикация</ProcessImage>
                <ProcessContent>
                  <ProcessTitle>Тестирование и публикация</ProcessTitle>
                  <ProcessText>
                    Многоуровневое тестирование (unit, интеграционные, UX). Публикация в 4 маркетплейсах (App Store, Google Play, Huawei AppGallery, RuStore). Настройка мониторинга crash-логов.
                  </ProcessText>
                </ProcessContent>
              </ProcessCard>
            </ProcessGrid>
          </SectionContainer>
        </Section>

        {/* Галерея интерфейсов */}
        <Section style={{ backgroundColor: '#1A1A1A' }}>
          <SectionContainer>
            <SectionTitle
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
            >
              Интерфейсы «Моей Аптечки»
            </SectionTitle>
            <GalleryGrid
              as={motion.div}
              variants={containerVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              <GalleryItem variants={itemVariants}>
                <GalleryImage>Главный экран</GalleryImage>
                <GalleryCaption>
                  Удобный список препаратов
                </GalleryCaption>
              </GalleryItem>
              
              <GalleryItem variants={itemVariants}>
                <GalleryImage>Карточка лекарства</GalleryImage>
                <GalleryCaption>
                  Фото, инструкция, срок годности
                </GalleryCaption>
              </GalleryItem>
              
              <GalleryItem variants={itemVariants}>
                <GalleryImage>Уведомления</GalleryImage>
                <GalleryCaption>
                  Напоминания о приёме
                </GalleryCaption>
              </GalleryItem>
              
              <GalleryItem variants={itemVariants}>
                <GalleryImage>Семейный доступ</GalleryImage>
                <GalleryCaption>
                  Управление семейной аптечкой
                </GalleryCaption>
              </GalleryItem>
            </GalleryGrid>
          </SectionContainer>
        </Section>

        {/* Достижения */}
        <Section>
          <SectionContainer>
            <SectionTitle>Результаты проекта</SectionTitle>
            <AchievementsSection>
              <AchievementsList>
                <AchievementItem>Опубликовано в 4 интернет-магазинах</AchievementItem>
                <AchievementItem>Более X скачиваний (данные можно указать по согласию клиента)</AchievementItem>
                <AchievementItem>Приложение используется семьями и студентами-медиками</AchievementItem>
                <AchievementItem>Получены положительные отзывы о простоте и удобстве</AchievementItem>
              </AchievementsList>
              <RequestButton
                onClick={handleRequestResults}
                whileHover={{ scale: 1.02 }}
              >
                Запросить результаты по проекту
              </RequestButton>
            </AchievementsSection>
          </SectionContainer>
        </Section>

        {/* Заключение и призыв */}
        <ConclusionSection>
          <SectionContainer>
            <ConclusionTitle
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
            >
              Готовы создать медтех-продукт?
            </ConclusionTitle>
            <ConclusionText
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
            >
              Проект «Моя Аптечка» доказал: мы умеем работать с медтехом, где важна не только эстетика, но и безопасность. Мы готовы создавать приложения для здравоохранения и других высокоответственных сфер.
            </ConclusionText>
            <ConclusionCTA
              onClick={handleContactClick}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
              whileHover={{ scale: 1.02 }}
            >
              Заказать разработку медтех-продукта
            </ConclusionCTA>
            <NavigationLinks
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
            >
              <NavLink to="/projects">← Все проекты</NavLink>
              <NavLink to="/">Главная</NavLink>
            </NavigationLinks>
          </SectionContainer>
        </ConclusionSection>
      </main>
    </ProjectPageContainer>
  );
};

export default MoyaAptechkaPage;
